INSERT INTO `advertising` VALUES(2, '/admin/newspic/jiaodian/1336024216223.jpg', '', 'http://', 0, 0, '1336024222', 0, 0, '首页课程导航下广告');
INSERT INTO `bandr` VALUES(3, 1, 'JamesLoudspeaker', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。', 'James在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。James扬声器也是世界上最好的嵌入式扬声器产品之一！', '/bandr/JLS_LOGOk.jpg', '/bandr/63FXC.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(4, 1, 'GTL', 'GTL声学实验室于1976年创立于卡罗莱娜州，当时取名为Audio Excellence。公司成立后的第一个项目是帮两量豪华大巴安装高级音响系统。项目取得极大的成功，但GTL从此再也没做过豪华大巴的定制改装项目。他们热爱纯净、干净，超凡的音质效果，于是将工作的重心转移到生产超高质量家用扬声器。GTL的宗旨是生产永不过时的最佳扬声器。\r\n1982年，GTL设计了世界上首款多磁芯低音单元。这款单元的特别之处在于摒弃了传统单一大磁芯和单音圈的设计，取而代之以13个小型的磁芯和4个高密度音圈的组合。音质效果超凡脱俗，无可比拟，这种技术从来就没有其他任何一家扬声器厂商使用过。在2009年之前，这款低音单元是GTL私人审片室和家庭影院产品的基石。1985年，GTL开始为私人观影室设计扬声器系统。1999年，GTL设计了世界上首款编织型碳素纤维低音单元。2005年1月，将公司名称从Audio Excellence变更为GTL Sound Labs', 'GTL的产品只使用最好的单元，分配器，线材和阻尼材料。质量是GTL的印记！\r\n\r\n', '/bandr/GTLLogo.jpg', '/bandr/AP8251.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(5, 1, 'Krix', '公司成立于1974年，创始人Scott Krix，公司所在地：南澳大利亚洲首府阿德莱德市。公司宗旨：持续不断的支持遍及世界各地的客户和经销商，提供一流的研发设计，制造杰出的扬声器，并发展壮大Krix集团。所生产的产品销售至30多个国家。\r\n1980年 Scott发明了“音障墙”，并安装第一套商业影院扬声器系统到阿德莱德市的卡普里剧院。“音障墙”的发明，后来运用到澳大利亚的许多其它影院。\r\n1988年首次成功安装多厅影院扬声器到澳大利亚新南威尔士州HOYTS电影院。\r\n1993年Krix获得美国卢卡斯电影公司的第一个THX认证。 奎氏开始经营影院扬声器出口业务。2005 – Scott荣获澳大利亚电影业先驱者称号。2004年Krix举办30周年庆典，庆祝成为澳洲资历最老、规模最大，同时也是世界上第一家拥有THX认证并制造销售4路影院扬声器系统的制造商。2006年专利授予 “声角的恒定指向”。2007年专利授予 “声波引导表面的设计方法”。2010年Krix  X系列荣获CEDIA“价值超过10万美元的最佳家庭影院奖”。', '声学喇叭排列组合', '/bandr/KRIX_TAG_cmyk.jpg', '/bandr/FIX.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(6, 2, 'Audio Control', '公司已成立三十多年，由一批专业的音响工程师创立，主要设计并生产高性能的音频/视频设备，测试&声学校正仪器，影院处理器和放大器，多房间音频分配放大器，多区域音视频分配器。', '', '/bandr/AC_logo.jpg', '/bandr/control.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(7, 2, '安桥', '安桥的故事始于1905年，当时，一个名叫松下幸之助的小男孩在伍代自行车公司得到了一份兼职工作。松下幸之助和他的儿时好友伍代武（安桥创始人）在那非常努力地工作着。他们将伍代武的父亲让他们帮忙买烟的钱，通过批量采购赚取差价的方式积攒下来，拥有了人生中的第一桶金。随后，松下幸之助就建立了如今闻名世界的松下公司，而伍代武先生则成为其公司最顶尖的工程师之一。第二次世界大战后，松下决定拆分其音响部门。 1946年，伍代武先生抓住此机遇，建立了安桥株式会社大阪电器。由此，安桥诞生了！', '2000年后，安桥始终致力于高性能技术及创新。在这十年中，安桥发明了世界首套杜比定向逻辑II家庭影院系统，世界第一台具有以太网连接的家庭影院功放和世界上第一款7声道数字家庭影院功放。在中国，安桥推出了第一款全中文菜单的家庭影院功放。现在，安桥全系列产品都采用原装进口，为客户提供更加优质的产品。', '/bandr/onkyo-logo.jpg', '/bandr/onkyo-txnr809.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(8, 2, '天龙', '', '热情，艺术性，精锐技术 \r\n对Denon而言，音乐和影像的要求并不仅只是一门生意，开发出能带给视听者最佳影音体验的产品，需要的是源源不断的热情，也是企业永续经营的理由。致力于开发高效能的影音组件，比消费者早一步看到需求，便是我们身为领导品牌、并发挥创意的关键。随着音响工业进入新的世代，将移动科技融入生活，音乐的聆听地点、格式将不再受限。重现精确而纯净的音乐本质，丰富人们的生活，依然是Denon奉为不变的真理。 \r\n\r\n全面落实 \r\nDenon经营理念：热情、艺术性，和精锐技术，并不只是口号，更是在Denon的每位员工身体力行的内容。这三个要素，和百年来的创新历史和累积的技术实力，造就了Denon企业文化。产品中的每个小细节，都注入了相同的Denon理念，并以提供极致的影音娱乐体验，作为唯一的目标。 \r\n\r\n展望未来 \r\n拥有百年工艺，创下无数第一与经典的Denon，持续地将影音技术推向顶峰。一路走来珍贵的足迹，都将转化为迎向未来的动力。我们珍惜这份资产，也以此激励自己更臻完美。除了专精于产品核心的功率、精确度和效能，Denon也不断再突破，将崭新概念落实到新产品设计。无论潮流如何演变，Denon总是位居领先，继续写下璀璨的第一传奇。', '/bandr/denon-logo.jpg', '/bandr/denon-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(9, 3, 'PD', '', 'Projectiondesign是来自投影技术发源地挪威的一家专注高品质投影机研发和制造的厂商。所有PD的投影机均在挪威手工精制。大师级的工艺设计以及精选签名系列镜头让PD投影机成为投影机之中的奢侈品牌。', '', '/bandr/pd-prismahd.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(10, 3, 'Runco', '', 'Runco是美国家用投影机第一品牌！从上世纪80年代开始提出家庭影院投影机的概念，出产第一台家庭影院投影机，到如今形成5大系列30余款产品的庞大阵容，是家用投影机当之无愧的王者！', '', '/bandr/runco-ls10.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(12, 3, '富可视', '  富可视公司（InFocus）是全球数字投影机及其技术的开发、生产和销售的领导者.近20年来富可视致力于通过自己的产品，以精彩的方式帮助人们呈现自己的卓越创意，使之达到沟通或娱乐的目的——无论在会议室、演示现场、教室还是您温馨的家里，精彩无处不在。\r\n    \r\n', '富可视各类产品齐全，可充分满足所有市场领域的需求，在AV、企业、教育及家庭影院等市场领域，富可视为自己能够满足广泛的需要和要求而感到自豪。公司总部位于俄勒冈，并在北美、欧洲及亚洲地区设立办事处。', '/bandr/infocus-logo.jpg', '/bandr/infocus-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(13, 3, '三菱', '有139年历史。三菱电机始终致力于尖端技术及专门知识的研究开发活动，并且在此基础上从事高性能产品及设备系统的开发和制造。', '', '/bandr/sanling-logo.jpg', '/bandr/sanling-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(14, 3, '夏普', '1912年 公司正式成立 　　\r\n1981年 开始进入中国市场 　　\r\n1989年 推出世界上第一台单片LCD视频投影机 　　\r\n1999年 推出世界上第一台16:9 DLP数据投影机                  2001年推出了号称单枪之王的一代名机 Z9000,当年台湾发烧教父刘汉盛都是拿和 BARCO CINE6 三枪同台竞技，而两者各有千秋，历史上第一次有单枪和新型号三枪相比啊！所以长时间都是“史上最强单枪”的封号！　　\r\n 2006年推出1080P机王Z20000，代表了日系家用机的最高水准。　　\r\n  2011年在CES展会上推出全球第一台全高清3D投影机Z17000，荣获2011 CES最佳3D投影表现奖                 2012年再接再励，推出旗舰机型,,夏普Z30000A      ', 'xiapu-logo', '/bandr/xiapu-123.jpg', '', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(15, 3, '哈克尼斯', '', '哈克尼斯屏幕(Harkness Screen)公司是专业生产哈克尼斯各种银幕的生产厂家，是全球最大的银幕生产供应商。哈克尼斯银幕的生产已有八十多年的历史，产品被广泛使用于全世界各种不同应用场合，公司主要生产银幕，框架和银幕的遮幅系统；还生产用于室内和露天用途的折叠和充气银幕，广泛应用于电影、电视和剧院和家庭影院等休闲娱乐场所。其公司总部设在英国，在英国，法国和美国设有生产厂，在全世界有经销商。哈克尼斯产品已经在中国内地一线城市的众多五星级影院和IMAX影院得到广泛应用，例如万达、中影国际、橙天嘉禾、金逸、星美国际等。在中高端院线市场上占据着70%左右的市场份额。', '/bandr/smartview-logo.jpg', '/bandr/smartview-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(16, 3, '亿立屏幕', 'Elite Screens亿立是全球投影屏幕品牌，2004成立于美国加州，并在中国、台湾、日本、欧洲、法国等设立分支机构。有着10个据点, 約50個代理商, 約2500个经销商，持续致力于投影屏幕研发、生产、营销 和服务。', '', '/bandr/elitescreens-logo.jpg', '/bandr/elitescreens-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(17, 5, 'Control4', '亿立屏幕也是欧美CEA, Infocomm和CEDIA从2004以来的会员，此会员专门针对消费性产品，商用专业产品和高级家影产品的专业会员。亿立能提供真正消费者所需要的高性价比，而不只是单纯的屏幕作制造厂。我们的使命和目标永远都是-为专业投影屏幕而努力。', '', '/bandr/Control4.jpg', '/bandr/7-in-wall.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(18, 5, 'CardAccess', '', 'CardAccess产品具有智能，稳固，易安装，易操作等优点', '/bandr/Card_Access_Logo_Black_Red.jpg', '/bandr/WCS10-R-X-ZP.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(19, 5, 'Lutron', '路创于1961年发明了世界上第一台固态调光器。此后，路创在灯光控制领域获得了一系列的突破，包括第一台多场景预设灯光控制器，第一台无线射频灯光控制系统等等。', '路创通过ISO9001 的认证向客户作出保证，路创设计和制造所有自家的产品，并保持始终一致的产品质量。路创的调光器可节省能源，并延长灯泡的寿命。产品领域涉及到家庭和住宅；企业办公楼和政府大楼；酒店；商店；餐馆和娱乐场所等领域。', '/bandr/lutron_logo_int.jpg', '/bandr/HWI-CCO-8.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(20, 12, 'Napa Sound Labs', 'Napa Sound Labs是JamesLoudspeaker开发的新品牌。', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。公司固定员工为20人左右，年产值约为1千万美金，年产量约为7000只音箱，其中95%为嵌入式音箱。', '', '/bandr/od87-70.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(21, 12, 'JamesLoudspeaker', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。', '在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。公司固定员工为20人左右，年产值约为1千万美金，年产量约为7000只音箱，其中95%为嵌入式音箱。者尼公司是James在亚洲市场的第一个合作伙伴，拥有中国大陆，港澳台及新加坡的独家经销权。', '', '/bandr/31ODS70.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(22, 8, 'ASC', 'ASC公司全名为Acoustic Sciences Corporation,公司成立于1984年，公司慢慢发展成为专业的声学设计及制造声学产品的公司。', '公司所有产品都是在美国全手工打造。公司已经开发出上百种声学及噪音控制产品。', '/bandr/ASCLogo.jpg', '/bandr/WDSQ4WallDamp.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(23, 8, 'Kinetics', 'Kinetics公司专注于提供震动及噪音控制技术，业务遍及美国50个洲，加拿大及东南亚。代理商遍及中东，欧洲，南美洲，其他东南亚地区。', '公司主推产品为隔音防震产品；浮动地板；可移动屋顶绿化；轻型间墙；活动间墙；新型吊顶；防火吸音产品等。其隔音防震产品中浮动地台可隔78分贝噪音；防震隔音垫块片状高密度玻璃棉不会有老化现象。', '/bandr/kineticslogofromUSA.jpg', '', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(24, 7, 'TVIX', '', '', '/bandr/logo_TVIX.jpg', '/bandr/SlimS1.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(25, 7, '先锋', '先锋电子（中国）投资有限公司是 2001年4月16日由日本先锋株式会社出资成立。是日本先锋株式会社在中国、香港地区所投资的包括销售、研发、生产领域的14家公司之一。', '先锋中国是先锋品牌在中国地区的统括公司，负责中国地区的统括业务及先锋香港产品销售业务。本着“与更多人分享感动”的企业精神，不断推出高性能产品，努力创造永恒的视听奇迹，成为全球娱乐领域中的领航先锋。\r\n\r\n　　先锋是全球影音娱乐及光盘技术的先驱，运用先进科技投入家用影音、车用影音以及商业用市场，展现对消费者的热情与娱乐事业的重视。\r\n\r\n　　先锋中国所销售的产品包括家用影音产品、车用影音产品、车载导航产品及商用影视系统。家用影音产品包括DVD录像机、DVD影碟机、BD播放机、一体式家庭影院、分体式家庭影院、功率放大器、扬声器、耳机以及世界上先进的等离子电视机。车用影音产品有AV系统、CD播放机、扬声器系统、汽车音响功放系统等。AV一体化DVD车载导航产品将GPS导航、实时路况服务等先进信息导航功能与强势的影音播放娱乐功能结合在一起，为中国的车用多媒体领域开创了一个新的纪元。商业用产品包括IT相关产品以及CDJ调音系统等。先锋IT外围事业部不仅有效地整合了DVD产业的各项资源优势扶植全球DVD市场并以DVD技术、制造、与专业团队的优势，提供全球DVD-R/RW产品OEM市场、激光头等三大营运范畴。先锋的产品不仅拥有先进的功能，在外形设计上更是引导着消费新潮流。家用音视设备的Slim&Smart化已成为同行业的典范。另外，有机显示技术在车用系统中的应用也已为驾车者提供了另一番时尚便捷、自由舒适娱乐天地。', '/bandr/pioneer-logo.jpg', '/bandr/pioneer-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(26, 7, 'oppo', 'OPPO Digital公司是开设在美国加州矽谷的一家专门制作高端音视频数码视听产品的公司位于美国高新技术区，近水楼台先得月，自然易于取得和利用先进的图像、声音提取和还原技术，加上有机械加工手段完备的中国大型制造基地为强力后盾，因而能够形成自己的独特优势，不仅在美国市场站稳脚跟，并获得良好的声誉。 ', 'OPPO Digital 公司成立于2005年，在短短几年时间内，每年都会推出一款新产品，而且每款产品皆为同价位产品中的佼佼者，性能优良而售价又相对便宜，因而收到了美国广大消费者的欢迎，获得了市场的一致好评。 ', '/bandr/oppo-logo.jpg', '/bandr/oppo-bdp93.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(27, 6, 'Liberty', '', '', '/bandr/LibertyAVSolutions_Logo.jpg', '/bandr/12-2P-UC-RAN.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(28, 6, 'FURMAN', 'FURMAN品牌历史超过三十五年来，Furman一直是同行业中最值得信赖的名字，为音频、视频及广播专业人士在AC电源调节、校准、平衡隔离变压器、时序和分布等各方面提供保证。', '客户包括可敬的职业音乐家、著名的录音室、商业承包商，以及在全世界举办大型巡回演唱会的演出团体。他们选择Furman产品，是因为我们拥有着长期可靠的声誉和在工程上的专业成就，一直专注于满足专业人士的特殊需求，了解这一行业中设备故障和停机是无法承受的。', '/bandr/furman-logo.jpg', '/bandr/furman-ac210e.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(29, 6, 'Maxvision', '', '1>公司产品全部出自曾任职外企十多年的专职RD工程师精心设计并不断修正,真正满足定制影音线缆的各项性能所需;2>公司所有产品全部实行样品式定单工艺,绝对不做批量定单,众所周知成熟产品只有样品式生产工艺才能提升整个产品附加阶值;3>公司产品完全支持点对点量身定制,以期能迎合你的高贵选择和品位所需,这在业界是绝无仅有的', '/bandr/maxvision-logo.jpg', '/bandr/maxvision-1.5.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(30, 7, 'bypass', 'bypass公司是以生產數位回音.殘響混音效果器 ,音樂 /麥克風動態擴展器 , 高品質的擴大機和舞台專業音響聞名全球。自1998 年開始結合美國 Meret Audio 公司與英國 Greness 公司合作研發Hi-Fi 和KARAOKE 多功能Hi-End喇叭深受好評。 \r\n', '2007年進入中國市場,就以多款32位元回音.殘響混音效果器,深受專業舞台、卡拉OK業者的青睞! 2012年由日本引進最新Space Echo空間回音殘響混音效果器.及Feedback Anti 限頻加移頻反饋壓制效果版,將會再創前級效果器的另一個巔峰！', '/bandr/bypass-logo.jpg', '/bandr/bypass-dsp100se.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(31, 7, '舒尔话筒', '', 'SHURE品牌超过80多年历史，国际知名品牌，是话筒行业里旗舰性产品，产品广泛应用于各种演出、电台及娱乐、会议等一切需要声音的场所', '/bandr/shure-logo.jpg', '/bandr/shure-pg288.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(32, 7, '奥雷', 'AUDIOCENTER——具有80多年悠久历史的德国“奥雷”(AURE)公司旗下重金打造的专属高端音响品牌。它诠释出——在全球“音频”(AUDIO)领域中，尤其在当今电子科技高速发展、计算机科技史无前例的广泛应用并日新月异的大时代背景下，它将以“奥雷”为引爆“中心”(CENTER)，把中国高端专业音响乃至全球音响继续引领至一个全新的高度科技化、人性化和时尚奢华的行业地带，同时更将把普及优秀的音响文化作为在大中华区域的一项神圣历史使命……\r\n', '作为有着80多年专业制造历史、历经数次收购合并、不断发展壮大的德国“奥雷”(AURE)公司中的一员，“AUDIOCENTER”与所有欧洲高端音响品牌一样，无疑也饱含着优秀的制造和先进的科技“基因”，传承了德意志民族特有的严谨、细致、专注，奉行着“只做精品”的理念，已为全球客户打造了数十款的经典音响产品。今天， AUDIOCENTER“奥雷”产品遍布欧洲大陆乃至全球市场，尤其在高端娱乐和市政工程领域，AUDIOCENTER“奥雷”以专业、人性化的产品结构与一体化的销售概念，服务于全球最高端的专业人群，让全世界都能感受到AUDIOCENTER“奥雷”的专业、品质与服务。', '/bandr/audiooenter-logo.jpg', '/bandr/audiooenter-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(33, 7, '雷客', '\r\n     雷客由业内顶尖的北京雷石公司和台湾开曼天硕公司强强联手创立。主要致力于家庭卡拉OK视频点播设备的研发和应用，主要产品有“惊艳”、“精灵”、“婚庆”系列机顶盒点歌机、无线点歌掌中宝，触摸屏点歌台等卡拉OK点歌设备以及成功研发基于IOS和Andriod平台的“K-Pad”、“KKPhone”软件等具有前沿科技的K-pad、kkphone等点歌软件，引领家庭卡拉OK行业迈入新时代。', '雷客点歌机是一款目前最先进的点歌系统之一，机顶盒外观采用蝶形设计，高档时尚，能够与家庭装修完美融合，相得益彰。与移动点歌设备无线掌中宝或触摸式点歌台搭配使用，共同为您打造独一无二的家庭KTV视听空间，让您完美享受家庭K歌盛宴。', '/bandr/lieke-logo.jpg', '/bandr/lieke-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(34, 8, 'LHZ', '南京三良兴达声学技术有限公司。2010年，由一个拥有多年高级影音室设计及施工经验的团队成立，2010年，同期创办了 LHZ 品牌，并通过相关注册！\r\n', '视听室深度隔音解决方案及民用住宅室内声学解决方案提供，我们致力于可以给工程商提供最便捷的个性化快速施工的可实现性！', '/bandr/lhz-logo.jpg', '/bandr/lhz-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(36, 13, 'Panamorph', 'Panamorph是来自美国的专业级家用电影变形镜头的厂商。', '', '', '/bandr/panamorph-uv200.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(37, 11, '霍尼韦尔', '成立于1885年，被美国"财富"在杂志评为最受推崇的20家高科技企业之一', '霍尼韦尔国际（Honeywell International）是一家营业额达300亿美元的多元化高科技和制造企', '/bandr/honeywell-logo.jpg', '/bandr/honeywell-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(38, 11, 'OPTEX', 'OPTEX成立于1979年，是一家专业从事红外线技术研发与应用的公司，并相继在美国、英国、法国、韩国、香港等地成立了分支机构，提出ABC三重防御概念。', '', '/bandr/optex-logo.jpg', '/bandr/optex-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(39, 11, '福科斯', '', '“ＦＯＣＵＳ”是美安公司在美国注册的全球性品牌，已经通过ISO9001：2000国际质量体系认证和ＣＥ、３Ｃ等强制性认证', '/bandr/focus.jpg', '/bandr/focus-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr` VALUES(40, 11, '松下', '', 'Panasonic成立于1918年，世界五百强企业，品牌产品涉及家电、数码视听电子、办公产品、航空等诸多领域而享誉全球', '/bandr/songxia-logo.jpg', '/bandr/songxia-123.jpg', '2012-05-17 17:12:01', 0);
INSERT INTO `bandr1` VALUES(3, 1, 'JamesLoudspeaker', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。', 'James在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。James扬声器也是世界上最好的嵌入式扬声器产品之一！', '/bandr/JLS_LOGOk.jpg', '/bandr/63FXC.jpg', '', 0);
INSERT INTO `bandr1` VALUES(4, 1, 'GTL', 'GTL声学实验室于1976年创立于卡罗莱娜州，当时取名为Audio Excellence。公司成立后的第一个项目是帮两量豪华大巴安装高级音响系统。项目取得极大的成功，但GTL从此再也没做过豪华大巴的定制改装项目。他们热爱纯净、干净，超凡的音质效果，于是将工作的重心转移到生产超高质量家用扬声器。GTL的宗旨是生产永不过时的最佳扬声器。\r\n1982年，GTL设计了世界上首款多磁芯低音单元。这款单元的特别之处在于摒弃了传统单一大磁芯和单音圈的设计，取而代之以13个小型的磁芯和4个高密度音圈的组合。音质效果超凡脱俗，无可比拟，这种技术从来就没有其他任何一家扬声器厂商使用过。在2009年之前，这款低音单元是GTL私人审片室和家庭影院产品的基石。1985年，GTL开始为私人观影室设计扬声器系统。1999年，GTL设计了世界上首款编织型碳素纤维低音单元。2005年1月，将公司名称从Audio Excellence变更为GTL Sound Labs', 'GTL的产品只使用最好的单元，分配器，线材和阻尼材料。质量是GTL的印记！\r\n\r\n', '/bandr/GTLLogo.jpg', '/bandr/AP8251.jpg', '', 0);
INSERT INTO `bandr1` VALUES(5, 1, 'Krix', '公司成立于1974年，创始人Scott Krix，公司所在地：南澳大利亚洲首府阿德莱德市。公司宗旨：持续不断的支持遍及世界各地的客户和经销商，提供一流的研发设计，制造杰出的扬声器，并发展壮大Krix集团。所生产的产品销售至30多个国家。\r\n1980年 Scott发明了“音障墙”，并安装第一套商业影院扬声器系统到阿德莱德市的卡普里剧院。“音障墙”的发明，后来运用到澳大利亚的许多其它影院。\r\n1988年首次成功安装多厅影院扬声器到澳大利亚新南威尔士州HOYTS电影院。\r\n1993年Krix获得美国卢卡斯电影公司的第一个THX认证。 奎氏开始经营影院扬声器出口业务。2005 – Scott荣获澳大利亚电影业先驱者称号。2004年Krix举办30周年庆典，庆祝成为澳洲资历最老、规模最大，同时也是世界上第一家拥有THX认证并制造销售4路影院扬声器系统的制造商。2006年专利授予 “声角的恒定指向”。2007年专利授予 “声波引导表面的设计方法”。2010年Krix  X系列荣获CEDIA“价值超过10万美元的最佳家庭影院奖”。', '声学喇叭排列组合', '/bandr/KRIX_TAG_cmyk.jpg', '/bandr/FIX.jpg', '', 0);
INSERT INTO `bandr1` VALUES(6, 2, 'Audio Control', '公司已成立三十多年，由一批专业的音响工程师创立，主要设计并生产高性能的音频/视频设备，测试&声学校正仪器，影院处理器和放大器，多房间音频分配放大器，多区域音视频分配器。', '', '/bandr/AC_logo.jpg', '/bandr/control.jpg', '', 0);
INSERT INTO `bandr1` VALUES(7, 2, '安桥', '安桥的故事始于1905年，当时，一个名叫松下幸之助的小男孩在伍代自行车公司得到了一份兼职工作。松下幸之助和他的儿时好友伍代武（安桥创始人）在那非常努力地工作着。他们将伍代武的父亲让他们帮忙买烟的钱，通过批量采购赚取差价的方式积攒下来，拥有了人生中的第一桶金。随后，松下幸之助就建立了如今闻名世界的松下公司，而伍代武先生则成为其公司最顶尖的工程师之一。第二次世界大战后，松下决定拆分其音响部门。 1946年，伍代武先生抓住此机遇，建立了安桥株式会社大阪电器。由此，安桥诞生了！', '2000年后，安桥始终致力于高性能技术及创新。在这十年中，安桥发明了世界首套杜比定向逻辑II家庭影院系统，世界第一台具有以太网连接的家庭影院功放和世界上第一款7声道数字家庭影院功放。在中国，安桥推出了第一款全中文菜单的家庭影院功放。现在，安桥全系列产品都采用原装进口，为客户提供更加优质的产品。', '/bandr/onkyo-logo.jpg', '/bandr/onkyo-txnr809.jpg', '', 0);
INSERT INTO `bandr1` VALUES(8, 2, '天龙', '', '热情，艺术性，精锐技术 \r\n对Denon而言，音乐和影像的要求并不仅只是一门生意，开发出能带给视听者最佳影音体验的产品，需要的是源源不断的热情，也是企业永续经营的理由。致力于开发高效能的影音组件，比消费者早一步看到需求，便是我们身为领导品牌、并发挥创意的关键。随着音响工业进入新的世代，将移动科技融入生活，音乐的聆听地点、格式将不再受限。重现精确而纯净的音乐本质，丰富人们的生活，依然是Denon奉为不变的真理。 \r\n\r\n全面落实 \r\nDenon经营理念：热情、艺术性，和精锐技术，并不只是口号，更是在Denon的每位员工身体力行的内容。这三个要素，和百年来的创新历史和累积的技术实力，造就了Denon企业文化。产品中的每个小细节，都注入了相同的Denon理念，并以提供极致的影音娱乐体验，作为唯一的目标。 \r\n\r\n展望未来 \r\n拥有百年工艺，创下无数第一与经典的Denon，持续地将影音技术推向顶峰。一路走来珍贵的足迹，都将转化为迎向未来的动力。我们珍惜这份资产，也以此激励自己更臻完美。除了专精于产品核心的功率、精确度和效能，Denon也不断再突破，将崭新概念落实到新产品设计。无论潮流如何演变，Denon总是位居领先，继续写下璀璨的第一传奇。', '/bandr/denon-logo.jpg', '/bandr/denon-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(9, 3, 'PD', '', 'Projectiondesign是来自投影技术发源地挪威的一家专注高品质投影机研发和制造的厂商。所有PD的投影机均在挪威手工精制。大师级的工艺设计以及精选签名系列镜头让PD投影机成为投影机之中的奢侈品牌。', '', '/bandr/pd-prismahd.jpg', '', 0);
INSERT INTO `bandr1` VALUES(10, 3, 'Runco', '', 'Runco是美国家用投影机第一品牌！从上世纪80年代开始提出家庭影院投影机的概念，出产第一台家庭影院投影机，到如今形成5大系列30余款产品的庞大阵容，是家用投影机当之无愧的王者！', '', '/bandr/runco-ls10.jpg', '', 0);
INSERT INTO `bandr1` VALUES(12, 3, '富可视', '  富可视公司（InFocus）是全球数字投影机及其技术的开发、生产和销售的领导者.近20年来富可视致力于通过自己的产品，以精彩的方式帮助人们呈现自己的卓越创意，使之达到沟通或娱乐的目的——无论在会议室、演示现场、教室还是您温馨的家里，精彩无处不在。\r\n    \r\n', '富可视各类产品齐全，可充分满足所有市场领域的需求，在AV、企业、教育及家庭影院等市场领域，富可视为自己能够满足广泛的需要和要求而感到自豪。公司总部位于俄勒冈，并在北美、欧洲及亚洲地区设立办事处。', '/bandr/infocus-logo.jpg', '/bandr/infocus-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(13, 3, '三菱', '有139年历史。三菱电机始终致力于尖端技术及专门知识的研究开发活动，并且在此基础上从事高性能产品及设备系统的开发和制造。', '', '/bandr/sanling-logo.jpg', '/bandr/sanling-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(14, 3, '夏普', '1912年 公司正式成立 　　\r\n1981年 开始进入中国市场 　　\r\n1989年 推出世界上第一台单片LCD视频投影机 　　\r\n1999年 推出世界上第一台16:9 DLP数据投影机                  2001年推出了号称单枪之王的一代名机 Z9000,当年台湾发烧教父刘汉盛都是拿和 BARCO CINE6 三枪同台竞技，而两者各有千秋，历史上第一次有单枪和新型号三枪相比啊！所以长时间都是“史上最强单枪”的封号！　　\r\n 2006年推出1080P机王Z20000，代表了日系家用机的最高水准。　　\r\n  2011年在CES展会上推出全球第一台全高清3D投影机Z17000，荣获2011 CES最佳3D投影表现奖                 2012年再接再励，推出旗舰机型,,夏普Z30000A      ', 'xiapu-logo', '/bandr/xiapu-123.jpg', '', '', 0);
INSERT INTO `bandr1` VALUES(15, 3, '哈克尼斯', '', '哈克尼斯屏幕(Harkness Screen)公司是专业生产哈克尼斯各种银幕的生产厂家，是全球最大的银幕生产供应商。哈克尼斯银幕的生产已有八十多年的历史，产品被广泛使用于全世界各种不同应用场合，公司主要生产银幕，框架和银幕的遮幅系统；还生产用于室内和露天用途的折叠和充气银幕，广泛应用于电影、电视和剧院和家庭影院等休闲娱乐场所。其公司总部设在英国，在英国，法国和美国设有生产厂，在全世界有经销商。哈克尼斯产品已经在中国内地一线城市的众多五星级影院和IMAX影院得到广泛应用，例如万达、中影国际、橙天嘉禾、金逸、星美国际等。在中高端院线市场上占据着70%左右的市场份额。', '/bandr/smartview-logo.jpg', '/bandr/smartview-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(16, 3, '亿立屏幕', 'Elite Screens亿立是全球投影屏幕品牌，2004成立于美国加州，并在中国、台湾、日本、欧洲、法国等设立分支机构。有着10个据点, 約50個代理商, 約2500个经销商，持续致力于投影屏幕研发、生产、营销 和服务。', '', '/bandr/elitescreens-logo.jpg', '/bandr/elitescreens-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(17, 5, 'Control4', '亿立屏幕也是欧美CEA, Infocomm和CEDIA从2004以来的会员，此会员专门针对消费性产品，商用专业产品和高级家影产品的专业会员。亿立能提供真正消费者所需要的高性价比，而不只是单纯的屏幕作制造厂。我们的使命和目标永远都是-为专业投影屏幕而努力。', '', '/bandr/Control4.jpg', '/bandr/7-in-wall.jpg', '', 0);
INSERT INTO `bandr1` VALUES(18, 5, 'CardAccess', '', 'CardAccess产品具有智能，稳固，易安装，易操作等优点', '/bandr/Card_Access_Logo_Black_Red.jpg', '/bandr/WCS10-R-X-ZP.jpg', '', 0);
INSERT INTO `bandr1` VALUES(19, 5, 'Lutron', '路创于1961年发明了世界上第一台固态调光器。此后，路创在灯光控制领域获得了一系列的突破，包括第一台多场景预设灯光控制器，第一台无线射频灯光控制系统等等。', '路创通过ISO9001 的认证向客户作出保证，路创设计和制造所有自家的产品，并保持始终一致的产品质量。路创的调光器可节省能源，并延长灯泡的寿命。产品领域涉及到家庭和住宅；企业办公楼和政府大楼；酒店；商店；餐馆和娱乐场所等领域。', '/bandr/lutron_logo_int.jpg', '/bandr/HWI-CCO-8.jpg', '', 0);
INSERT INTO `bandr1` VALUES(20, 12, 'Napa Sound Labs', 'Napa Sound Labs是JamesLoudspeaker开发的新品牌。', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。公司固定员工为20人左右，年产值约为1千万美金，年产量约为7000只音箱，其中95%为嵌入式音箱。', '', '/bandr/od87-70.jpg', '', 0);
INSERT INTO `bandr1` VALUES(21, 12, 'JamesLoudspeaker', 'JameLoudspeaker的前身是一家定制安装公司，后来转型成为一家定制型扬声器生产商, 包括室内用，室外用和船用扬声器。', '在美国、欧洲和大洋洲有500多家定制安装公司和顶级设计师使用James的产品。公司固定员工为20人左右，年产值约为1千万美金，年产量约为7000只音箱，其中95%为嵌入式音箱。者尼公司是James在亚洲市场的第一个合作伙伴，拥有中国大陆，港澳台及新加坡的独家经销权。', '', '/bandr/31ODS70.jpg', '', 0);
INSERT INTO `bandr1` VALUES(22, 8, 'ASC', 'ASC公司全名为Acoustic Sciences Corporation,公司成立于1984年，公司慢慢发展成为专业的声学设计及制造声学产品的公司。', '公司所有产品都是在美国全手工打造。公司已经开发出上百种声学及噪音控制产品。', '/bandr/ASCLogo.jpg', '/bandr/WDSQ4WallDamp.jpg', '', 0);
INSERT INTO `bandr1` VALUES(23, 8, 'Kinetics', 'Kinetics公司专注于提供震动及噪音控制技术，业务遍及美国50个洲，加拿大及东南亚。代理商遍及中东，欧洲，南美洲，其他东南亚地区。', '公司主推产品为隔音防震产品；浮动地板；可移动屋顶绿化；轻型间墙；活动间墙；新型吊顶；防火吸音产品等。其隔音防震产品中浮动地台可隔78分贝噪音；防震隔音垫块片状高密度玻璃棉不会有老化现象。', '/bandr/kineticslogofromUSA.jpg', '', '', 0);
INSERT INTO `bandr1` VALUES(24, 7, 'TVIX', '', '', '/bandr/logo_TVIX.jpg', '/bandr/SlimS1.jpg', '', 0);
INSERT INTO `bandr1` VALUES(25, 7, '先锋', '先锋电子（中国）投资有限公司是 2001年4月16日由日本先锋株式会社出资成立。是日本先锋株式会社在中国、香港地区所投资的包括销售、研发、生产领域的14家公司之一。', '先锋中国是先锋品牌在中国地区的统括公司，负责中国地区的统括业务及先锋香港产品销售业务。本着“与更多人分享感动”的企业精神，不断推出高性能产品，努力创造永恒的视听奇迹，成为全球娱乐领域中的领航先锋。\r\n\r\n　　先锋是全球影音娱乐及光盘技术的先驱，运用先进科技投入家用影音、车用影音以及商业用市场，展现对消费者的热情与娱乐事业的重视。\r\n\r\n　　先锋中国所销售的产品包括家用影音产品、车用影音产品、车载导航产品及商用影视系统。家用影音产品包括DVD录像机、DVD影碟机、BD播放机、一体式家庭影院、分体式家庭影院、功率放大器、扬声器、耳机以及世界上先进的等离子电视机。车用影音产品有AV系统、CD播放机、扬声器系统、汽车音响功放系统等。AV一体化DVD车载导航产品将GPS导航、实时路况服务等先进信息导航功能与强势的影音播放娱乐功能结合在一起，为中国的车用多媒体领域开创了一个新的纪元。商业用产品包括IT相关产品以及CDJ调音系统等。先锋IT外围事业部不仅有效地整合了DVD产业的各项资源优势扶植全球DVD市场并以DVD技术、制造、与专业团队的优势，提供全球DVD-R/RW产品OEM市场、激光头等三大营运范畴。先锋的产品不仅拥有先进的功能，在外形设计上更是引导着消费新潮流。家用音视设备的Slim&Smart化已成为同行业的典范。另外，有机显示技术在车用系统中的应用也已为驾车者提供了另一番时尚便捷、自由舒适娱乐天地。', '/bandr/pioneer-logo.jpg', '/bandr/pioneer-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(26, 7, 'oppo', 'OPPO Digital公司是开设在美国加州矽谷的一家专门制作高端音视频数码视听产品的公司位于美国高新技术区，近水楼台先得月，自然易于取得和利用先进的图像、声音提取和还原技术，加上有机械加工手段完备的中国大型制造基地为强力后盾，因而能够形成自己的独特优势，不仅在美国市场站稳脚跟，并获得良好的声誉。 ', 'OPPO Digital 公司成立于2005年，在短短几年时间内，每年都会推出一款新产品，而且每款产品皆为同价位产品中的佼佼者，性能优良而售价又相对便宜，因而收到了美国广大消费者的欢迎，获得了市场的一致好评。 ', '/bandr/oppo-logo.jpg', '/bandr/oppo-bdp93.jpg', '', 0);
INSERT INTO `bandr1` VALUES(27, 6, 'Liberty', '', '', '/bandr/LibertyAVSolutions_Logo.jpg', '/bandr/12-2P-UC-RAN.jpg', '', 0);
INSERT INTO `bandr1` VALUES(28, 6, 'FURMAN', 'FURMAN品牌历史超过三十五年来，Furman一直是同行业中最值得信赖的名字，为音频、视频及广播专业人士在AC电源调节、校准、平衡隔离变压器、时序和分布等各方面提供保证。', '客户包括可敬的职业音乐家、著名的录音室、商业承包商，以及在全世界举办大型巡回演唱会的演出团体。他们选择Furman产品，是因为我们拥有着长期可靠的声誉和在工程上的专业成就，一直专注于满足专业人士的特殊需求，了解这一行业中设备故障和停机是无法承受的。', '/bandr/furman-logo.jpg', '/bandr/furman-ac210e.jpg', '', 0);
INSERT INTO `bandr1` VALUES(29, 6, 'Maxvision', '', '1>公司产品全部出自曾任职外企十多年的专职RD工程师精心设计并不断修正,真正满足定制影音线缆的各项性能所需;2>公司所有产品全部实行样品式定单工艺,绝对不做批量定单,众所周知成熟产品只有样品式生产工艺才能提升整个产品附加阶值;3>公司产品完全支持点对点量身定制,以期能迎合你的高贵选择和品位所需,这在业界是绝无仅有的', '/bandr/maxvision-logo.jpg', '/bandr/maxvision-1.5.jpg', '', 0);
INSERT INTO `bandr1` VALUES(30, 7, 'bypass', 'bypass公司是以生產數位回音.殘響混音效果器 ,音樂 /麥克風動態擴展器 , 高品質的擴大機和舞台專業音響聞名全球。自1998 年開始結合美國 Meret Audio 公司與英國 Greness 公司合作研發Hi-Fi 和KARAOKE 多功能Hi-End喇叭深受好評。 \r\n', '2007年進入中國市場,就以多款32位元回音.殘響混音效果器,深受專業舞台、卡拉OK業者的青睞! 2012年由日本引進最新Space Echo空間回音殘響混音效果器.及Feedback Anti 限頻加移頻反饋壓制效果版,將會再創前級效果器的另一個巔峰！', '/bandr/bypass-logo.jpg', '/bandr/bypass-dsp100se.jpg', '', 0);
INSERT INTO `bandr1` VALUES(31, 7, '舒尔话筒', '', 'SHURE品牌超过80多年历史，国际知名品牌，是话筒行业里旗舰性产品，产品广泛应用于各种演出、电台及娱乐、会议等一切需要声音的场所', '/bandr/shure-logo.jpg', '/bandr/shure-pg288.jpg', '', 0);
INSERT INTO `bandr1` VALUES(32, 7, '奥雷', 'AUDIOCENTER——具有80多年悠久历史的德国“奥雷”(AURE)公司旗下重金打造的专属高端音响品牌。它诠释出——在全球“音频”(AUDIO)领域中，尤其在当今电子科技高速发展、计算机科技史无前例的广泛应用并日新月异的大时代背景下，它将以“奥雷”为引爆“中心”(CENTER)，把中国高端专业音响乃至全球音响继续引领至一个全新的高度科技化、人性化和时尚奢华的行业地带，同时更将把普及优秀的音响文化作为在大中华区域的一项神圣历史使命……\r\n', '作为有着80多年专业制造历史、历经数次收购合并、不断发展壮大的德国“奥雷”(AURE)公司中的一员，“AUDIOCENTER”与所有欧洲高端音响品牌一样，无疑也饱含着优秀的制造和先进的科技“基因”，传承了德意志民族特有的严谨、细致、专注，奉行着“只做精品”的理念，已为全球客户打造了数十款的经典音响产品。今天， AUDIOCENTER“奥雷”产品遍布欧洲大陆乃至全球市场，尤其在高端娱乐和市政工程领域，AUDIOCENTER“奥雷”以专业、人性化的产品结构与一体化的销售概念，服务于全球最高端的专业人群，让全世界都能感受到AUDIOCENTER“奥雷”的专业、品质与服务。', '/bandr/audiooenter-logo.jpg', '/bandr/audiooenter-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(33, 7, '雷客', '\r\n     雷客由业内顶尖的北京雷石公司和台湾开曼天硕公司强强联手创立。主要致力于家庭卡拉OK视频点播设备的研发和应用，主要产品有“惊艳”、“精灵”、“婚庆”系列机顶盒点歌机、无线点歌掌中宝，触摸屏点歌台等卡拉OK点歌设备以及成功研发基于IOS和Andriod平台的“K-Pad”、“KKPhone”软件等具有前沿科技的K-pad、kkphone等点歌软件，引领家庭卡拉OK行业迈入新时代。', '雷客点歌机是一款目前最先进的点歌系统之一，机顶盒外观采用蝶形设计，高档时尚，能够与家庭装修完美融合，相得益彰。与移动点歌设备无线掌中宝或触摸式点歌台搭配使用，共同为您打造独一无二的家庭KTV视听空间，让您完美享受家庭K歌盛宴。', '/bandr/lieke-logo.jpg', '/bandr/lieke-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(34, 8, 'LHZ', '南京三良兴达声学技术有限公司。2010年，由一个拥有多年高级影音室设计及施工经验的团队成立，2010年，同期创办了 LHZ 品牌，并通过相关注册！\r\n', '视听室深度隔音解决方案及民用住宅室内声学解决方案提供，我们致力于可以给工程商提供最便捷的个性化快速施工的可实现性！', '/bandr/lhz-logo.jpg', '/bandr/lhz-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(36, 13, 'Panamorph', 'Panamorph是来自美国的专业级家用电影变形镜头的厂商。', '', '', '/bandr/panamorph-uv200.jpg', '', 0);
INSERT INTO `bandr1` VALUES(37, 11, '霍尼韦尔', '成立于1885年，被美国"财富"在杂志评为最受推崇的20家高科技企业之一', '霍尼韦尔国际（Honeywell International）是一家营业额达300亿美元的多元化高科技和制造企', '/bandr/honeywell-logo.jpg', '/bandr/honeywell-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(38, 11, 'OPTEX', 'OPTEX成立于1979年，是一家专业从事红外线技术研发与应用的公司，并相继在美国、英国、法国、韩国、香港等地成立了分支机构，提出ABC三重防御概念。', '', '/bandr/optex-logo.jpg', '/bandr/optex-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(39, 11, '福科斯', '', '“ＦＯＣＵＳ”是美安公司在美国注册的全球性品牌，已经通过ISO9001：2000国际质量体系认证和ＣＥ、３Ｃ等强制性认证', '/bandr/focus.jpg', '/bandr/focus-123.jpg', '', 0);
INSERT INTO `bandr1` VALUES(40, 11, '松下', '', 'Panasonic成立于1918年，世界五百强企业，品牌产品涉及家电、数码视听电子、办公产品、航空等诸多领域而享誉全球', '/bandr/songxia-logo.jpg', '/bandr/songxia-123.jpg', '', 0);
