INSERT INTO `essay` VALUES(1, 2, '者尼简介', '', '', '<p style="text-align:left;">\r\n	<br />\r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;font-size:14px;">者尼——一个象征奢华与品位的品牌，成立于</span><span style="font-size:14px;">2007</span><span style="font-family:宋体;font-size:14px;">年，经过短短几年的飞速发展，现已成长为北京地区规模最大的家庭影院系统集成商，中国私人影院定制安装行业的领军企业。在项目管理以及客户服务等方面积累了丰富而成熟的经验，与</span><span style="font-size:14px;">2010</span><span style="font-family:宋体;font-size:14px;">年在山西设立分公司，并与上海，南京，成都等地经销商达成战略合作关系。</span><span></span> \r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<span style="font-family:宋体;font-size:14px;">者尼私人影院作为私人影院倡导者，一直致力于向用户传播高品质的生活理念，主要承接别墅和会所性质的私人影院工程，提供整体化解决方案。其中包括专业声光学设计服务，智能中控编程服务，音视频调试服务及各类影音产品。</span><span></span> \r\n</p>\r\n<p style="text-indent:21pt;">\r\n	<span style="font-family:宋体;font-size:14px;">者尼私人影院，不是简单地售出产品给终端客户，而是为了客户度身定制私人影音娱乐整体解决方案，从工程设计，产品选购，安装调试，到用户培训，维修保养，系统升级等全方位一站式服务，为客户打造个性影音娱乐生活，使影音产品和顾客的家居布置融为一体。</span> \r\n</p>\r\n<p style="text-indent:21.0pt;">\r\n	<br />\r\n<span style="font-family:宋体;font-size:14px;"></span><span></span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:16px;"><strong>公司定位：</strong></span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">高级家庭影音系统集成商</span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">提供家庭影音整体化解决方案</span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-family:宋体;font-size:14px;"></span><span></span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:16px;"><strong>经营理念：</strong></span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">致力于为客户提供高品质家庭影音娱乐系统，整体化解决方案，提升客户的生活品质.</span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-family:宋体;font-size:14px;"></span><span></span> \r\n</p>\r\n<p>\r\n	<span> </span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:16px;"><strong>业务范围：</strong></span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">家庭影音娱乐系统</span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">高级背景音乐系统</span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">多房间灯光控制系统</span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">家庭智能系统</span><span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;font-size:14px;">安防监控</span><span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', 0, '2012-05-16 09:24:25', 0, 0, '', '', '', '', '');
