INSERT INTO `essay` VALUES(18, 3, '《数字家庭》关于者尼HAA培训的报道', '', '', '<p>\r\n	<span style="font-size:20px;"><span style="font-size:16px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随着定制安装与私人影院的兴起，视听室的设计与视听系统的调试对相关从业者提出了更高的要求。成长的市场孕育了高要求的客户，中国需要高素质的相关从业人员对为他们服务，这就是《数字家庭》联手者尼将HAA培训及联盟引入中国的原因。</span></span>\r\n</p>\r\n<p style="text-align:center;">\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516091340_72336.jpg" alt="" />\r\n</p>\r\n<p style="text-align:center;">\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516091347_32326.jpg" alt="" />\r\n</p>\r\n<p style="text-align:center;">\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516091354_12389.jpg" alt="" />\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img src="/admin/hjd_Ub/attached/image/20120516/20120516091402_48427.jpg" alt="" /><br />\r\n<span style="font-size:20px;"></span>\r\n</p>', 0, '2012-05-16 17:14:37', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(19, 3, '者尼蝉联CEDIA年度设计大奖最佳家庭影院项目冠军', '', '', '<span style="font-size:18px;"> 者尼私人影院优山美地项目荣获CEDIA协会2011年度最佳家庭影院大奖 \r\n（45-85万人民币组），此次获奖是者尼私人影院暨荣膺CEDIA协会2010年度最佳家庭影院项目之后第二次蝉联最佳家庭影院项目冠军。同时，者尼私\r\n人影院也是迄今为止唯一一家获得此项殊荣的亚洲私人影院定制安装公司！<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;感谢为者尼的每个项目付辛勤劳动的所有者尼人！</span> \r\n<p>\r\n	<span style="font-size:18px;">&nbsp;者尼优山美地项目荣获美国CEDIA协会2011年度设计大奖 <br />\r\n——最佳家庭影院项目（45-85万人民币）</span>\r\n</p>\r\n<div style="text-align:center;">\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516091528_34743.jpg" alt="" /><br />\r\n</div>', 0, '2012-05-16 17:15:31', 0, 0, '', '', '', '', '');
