INSERT INTO `essay` VALUES(46, 6, '', '/pic/1337071794429.jpg', 'pic/GD/13370717949344429.jpg', '', 0, '2012-05-15 16:49:54', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(48, 14, '月影花移动', '/pic/1337148831603.jpg', 'pic/GD/13371488315341603.jpg', '<p>\r\n	<span style="font-size:14px;">新古典风格</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">“形散神聚”是新古典风格的主要特点。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">在注重装饰效果的同事，用现代的手法和材质还原古典气质。新古典风格具备了古典与现代的双重审美效果，完美的结合也让人们在享受物质文明的同时得到了精神上的慰藉。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516061959_15191.jpg" alt="" /><img src="/admin/hjd_Ub/attached/image/20120516/20120516062015_77862.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516062033_76552.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516062403_44417.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">运用了古典元素的壁纸，金色的暗花，让整个房间有着强烈的色彩，反差和金属的味道。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516062517_20744.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">侧墙的声学处理材料巧妙遮挡在丝质帘幕之后</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516062605_33821.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">音响效果是根据美国杜比实验室最新研发的pro logic环绕声格式来设计的9.2声道家庭影院系统。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516062729_53461.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">房间的四周墙面按设计要求布置了专业声学处理材料。透过装饰丝帘，可以看到不同形状的声学扩散体和吸音体。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">在房间的中间布置了2D扩散体，让前方声场的声音更加均衡自然。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">在房间的高处布置了3D扩散体，用来加强环绕声效果。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">房间后墙中间的吸音模块用来提升声音的清晰度。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"></span>\r\n</p>', 0, '2012-05-16 14:30:27', 0, 0, '', '', '/pic/1337148831673.jpg', '/pic/1337148831161.jpg', '/pic/1337148831527.jpg');
INSERT INTO `essay` VALUES(49, 14, '简约派的心灵', '/pic/1337150290188.jpg', 'pic/GD/13371502901130188.jpg', '<p>\r\n	<span style="font-size:14px;">简约欧式风格沿袭古典欧式风格的主元素，在此基础上，融入现代的生活元素。欧式的居室有的不止是豪华大气，更多的是惬意和浪漫。通过完美的曲线，精益求精的细节处理，带给家人不尽的舒适触感。和谐是欧式风格的最高境界。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img style="width:480px;height:316px;" src="/admin/hjd_Ub/attached/image/20120516/20120516064114_66843.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img style="width:481px;height:316px;" src="/admin/hjd_Ub/attached/image/20120516/20120516064148_56054.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img style="width:482px;height:729px;" src="/admin/hjd_Ub/attached/image/20120516/20120516064215_10969.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img style="width:486px;height:727px;" src="/admin/hjd_Ub/attached/image/20120516/20120516064458_86072.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img style="width:488px;height:730px;" src="/admin/hjd_Ub/attached/image/20120516/20120516064408_88975.jpg" alt="" /><br />\r\n</span> \r\n</p>', 0, '2012-05-16 14:45:40', 0, 0, '', '', '/pic/1337150290843.jpg', '/pic/1337150290720.jpg', '/pic/1337150290688.jpg');
INSERT INTO `essay` VALUES(50, 15, '如梦随想', '/pic/1337151738402.jpg', 'pic/GD/13371517387038402.jpg', '<p style="text-align:center;">\r\n	<span style="font-size:14px;">造的是梦 </span><br />\r\n<span style="font-size:14px;"> 还是现实 </span><br />\r\n<span style="font-size:14px;"> 这里晴空万里 </span><br />\r\n<span style="font-size:14px;"> 苍穹共碧海一色 </span><br />\r\n<span style="font-size:14px;"> 一片令人情迷的 </span><br />\r\n<span style="font-size:14px;"> 梦寐之蓝</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> “一只蝴蝶在巴西轻拍翅膀，可以导致一个月后得克萨斯州的一场龙卷风。”这是蝴蝶效应。同样，一部热映的《阿凡达》电影也可以在全球引发各种连锁反应，这就是阿凡达效应。吴先生笑称自己的影院就是阿凡达效应的产物。</span><br />\r\n<span style="font-size:14px;"> 说起自己会所的这套阿凡达主题的定制影院，必需要从《阿凡达》这部电影说起。吴先生饶有兴致地为我们讲了他看电影《阿凡达》的经历。工作繁忙的吴先生其实\r\n根本无暇去电影院看电影，但是当周围所有的人都在议论卡梅隆、《阿凡达》和IMAX的时候，自己也被蛊惑了。随即驱车前往中国电影博物馆，去了之后才发现\r\n《阿凡达》这部电影已经让世界陷入了癫狂，即便是午夜场的影票也已售罄。 \r\n吴先生只得乘兴而来，败兴而归。就是那样一段经历，让吴先生有了拥有一个属于自己的专属电影院的梦想。</span><br />\r\n<span style="font-size:14px;"> 者尼私人影院作为高级私人影院设计与定制安装的领跑者，自然会倾尽全力帮助客户将梦想筑构成现实。</span><br />\r\n<span style="font-size:14px;"> 吴先生想把梦想建筑在北京西山自己的私人会所里，空间长6.4米、宽5.1米、面积30平米左右，可以方便朋友和家人在会所进行过打球、游泳等强度稍大的\r\n运动后，能在视听室里得到充分的休息和放松，所以对娱乐的要求会多一些。奢华是必不可少的，同时还要有很强的设计感。经过和设计师的斟酌，将风格定位为阿\r\n凡达蓝色太空舱经典主题影院。主题影院能够将那些足以撼动灵魂的梦境的美，经过分析与提炼，用视觉的、可触摸的具体方式，呈现。在设计师给客户提供了影音\r\n室效果图之后，客户被者尼私人影院设计师前卫的设计理念所折服，当下定下了展厅的主题风格。者尼私人影院在国内率先出主题私人影院的概念，这在当下中国影\r\n音行业尚属先例。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070315_55516.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 阿凡达主题影院</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 者尼私人影院一直认为，一间完美的私人影院不仅要能展现高品质声画效果，同时还需要具备赏心悦目的室内美学效果。进入阿凡达主题影院，大胆的蓝色调，给人\r\n强烈的科幻主义色彩。不规则几何形状被大量运用，营造精神世界的有力架构，为寂寞找寻最温暖的慰藉。整个影音室处于全地下室结构，避光条件比较好。室内整\r\n体采用了蓝色的吸音软包，绒布的反光比较小，能很好的还原画面对比度。同时，幕布自上到下的宽幅较大，大屏幕能很好地将蓝色的光打散，自然画面色彩也会很\r\n好。<br />\r\n最为醒目的，当属高悬的那片蔚蓝星空。灯光亮起，星光闪烁，眼睛会不自觉停留在那一片深深浅浅晶莹剔透的蓝色中……赋予你无限遐想，如同精灵般，难以琢磨\r\n和捕捉。忽然之间，宛若置身于阿凡达的潘多拉星球。未来，科幻主义必将成为空间中的绝对主角，在迷幻中寻找新生的力量。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070354_10727.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070411_15193.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 星空顶设计 <br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 在者尼的工程师去实地测量房间的时候，发现了一个突出的问题。这间主题影院要建在空旷的地下室，可是这间地下室的抽风设备噪音非常大。在遇到问题的时候，\r\n者尼作为国内顶级定制安装私人影院公司的专业性就体现出来了。经过实际测量之后，设计师在墙体方面做了实体结构双层中空24cm的墙体，水泥以及10cm\r\n的龙骨。墙体中间布置了吸音棉，用来控制房间中的反射声的能量，让声音听起来足够清晰。房间整体采用了吸音软包，凹凸有致的墙面设计，营造出太空舱的效\r\n果，同时将灯带巧妙隐藏于软包背面。<br />\r\n为保证隔音效果，设计师还在房间门口采用了双层隔音门的声闸设计，大幅度地提升了隔音质量。5.2声道全嵌入式James扬声器，让人置身\r\n于阿凡达的世界之中。美国的私人影院大部分都是隐藏式设计，因为吴先生的会所客人出入较为频繁，所以者尼设计师也采用全嵌入式的影院设计，这样可以避免人\r\n员频繁流动对器材产生损害。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070444_78755.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">产品选择——取舍之间、彰显智慧<br />\r\n体会完这间会所娱乐室所营造的浓浓的科幻色彩后，接下来我们就要看看其选用的设备是否能与这间影音室的气氛相得益彰。对于欣赏大片、引吭高歌、畅玩游戏来\r\n说，完美的影像同样需要强大的音响系统来支持。者尼设计师为客户搭配的是James扬声器、、Onkyo TX-SR807功放以及Wharfedale\r\n 3190卡拉OK音箱，这套音响系统的搭配足以将声音的临场感觉表达得淋漓尽致。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070509_71987.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> James 嵌顶音箱</span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"> </span>\r\n</p>\r\n<p>\r\n	James Louderspeaker5.2声道系统<br />\r\n美国极品嵌入式音箱品牌James \r\nLoudspeaker对于AV发烧友来说并不算陌生。James系列扬声器由美国著名扬声器设计大师Mike \r\nPark设计，他曾经在哈曼集团工作20余年，在任期间研发了K2、JBL SYSTHESIS \r\nthree等产品。在美国、欧洲以及亚太地区有500多家定制安装公司和顶级家庭影院设计师使用James产品，美国娱乐界、体育界很多知名人士家中的私\r\n人影院都使用James扬声器。James扬声器箱体部分以T6航天铝纯手工焊接制造，入墙工艺将箱体与墙面巧妙接壤，勾勒出深藏不露的低调，世界专利的\r\n铍合金高音系列提供精准声场还原，观看好莱坞大片时更是效果惊人——播放影片的时候它们几乎是“纤毫必现”地将所有的影片信息传达给听众，那种致密丰富的\r\n声音信息量让人吃惊，所有隐藏的声音细节都被完全展示了出来，那种饱含能量的听觉刺激不断地从四面八方包围而来有，其效果远远超越普通的商业电影院。\r\n</p>\r\n<p>\r\n	<br />\r\nOnkyo&nbsp; TX-SR807功放<br />\r\n在声音方面，除了做一些隔音、吸音、扩散等声学方面的处理，一部好的功放也会为音质的提高锦上添花。Onkyo \r\nTX-SR807声音瞬态响应速度很快、分析力高和冲击感强劲，干净利落，动态凌厉。还原音效强劲的电影片段时，Onkyo \r\nTX-SR807足以让你爆个翻天覆地。\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516070543_32656.jpg" alt="" />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070559_65434.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> Wharfedale 卡拉OK音箱</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 再看看另一件强大的多功能产品——卡拉OK触摸屏，影院摇身一变，成了练歌房，相信你一定会为这样专业的卡拉OK系统发出感叹，千万首歌，从现代到古典，\r\n从流行到民族，只要坐在沙发上轻轻触摸，周围就会弥漫起曼妙的音乐，在幕布的上方，设计师设计了两个独立的Wharfedale卡拉OK音箱，专供唱歌使\r\n用，让声音更加圆润富有质感。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070634_29585.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516070645_59548.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">富可视&nbsp; SP8682&nbsp; 1080P高清高亮投影机</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">吴先生在看过众多投影机之后，欧美风格的代表富可视SP8682带来的真实视感吸引了吴先生的眼球。拥有全高清物理分辨率的SP8682外形方方正正更显\r\n大气稳重。与以往的DLP机型相比，SP8682拥有5000:1的原生对比度，配合1200流明的输出亮度、6段RGB色轮技术以及Brilliant\r\nColor色彩处理技术，能带来更为出色的画面色彩再现能力。优秀的画面重塑能力，色彩饱满，各色阶过渡均匀，层次感、立体感以及景深感都尤其突出。在实\r\n试《叶问》的打斗场面上，东北大汉用后脚撩起砍刀并顺手接过砍向叶问的一霎，冷酷的金属刀声配合流畅的画面尤如真得要将眼前的屏幕砍开一样，音效和速度都\r\n达到令人满意的效果。</span> \r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516070744_45546.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516070759_32329.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 锐普 至尊FXZ9 120寸画框透声幕布</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 幕面雕刻半球面微镜纹，表面光洁柔滑，可有效消除眩光现象，同时极大提高幕布的视场角及解像力，还原色彩的饱和度，令画面更生动、更清晰 \r\n。在观看朗朗与波切利在意大利托斯卡纳（Tuscany）的演唱会上，画面不但呈现了郊野的辽阔，而且立体感层次表现突出，入夜后观众席上五彩缤纷的衣服\r\n和不同肤色的脸孔都能真实地呈现，可见SCREENPRO FXZ9120是一高性价比的利器。</span> \r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516070906_28912.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 飞利浦&nbsp;TSU9600&nbsp;3.7寸6万色触摸屏</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 在这里，我们先看看吴先生家的设备清单：一套James的音响系统，一台ZENE多媒体中心、一台富可视投影机、一台卡拉OK服务器，还有无数的灯光设备\r\n等，光是这些设备的遥控器就多达数件，在使用过程中难免显得繁乱复杂。但我们同时发现，在吴先生的影音室中没有看到一台遥控器，只有一个触控设备。是的，\r\n这就是启动影音室所有设备的大脑，一台PHILIPS的智能中控，兼容房间中的一切遥控设备，包括空调、灯光一切可遥控的设备。中控还设置了六路智能照明\r\n控制系统，六种常用照明模式，客户可根据喜好调整相应模式，不同娱乐模式会自动切换到相应的灯光场景。安装了这套智能影音系统以后，坐在沙发上无需起身，\r\n轻轻点击一下按键，应该打开的设备全部依次打开，所有需要关闭的设备全部依次关闭，便捷的同时也增加了使用的安全系数</span><br />\r\n<span style="font-size:14px;"> 一部电影结束之后，选一款Wii Sports体育游戏来舒缓一下紧张的情绪，享受“众乐乐”之乐。好的设计是有灵魂的，依附空间存在，与空间共生。在者尼私人影院中，人们更多感受到的，是惊喜，是舒适、自然，是一种对真正优质生活的追求与表达。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span><span style="font-size:14px;"></span> \r\n</p>', 0, '2012-05-16 15:10:27', 0, 0, '', '', '/pic/1337151738718.jpg', '/pic/1337151738965.jpg', '/pic/1337151738898.jpg');
INSERT INTO `essay` VALUES(51, 15, 'summer luxury', '/pic/1337152585493.jpg', 'pic/GD/13371525859745493.jpg', '<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 热烈的夏日，在绚烂里沉淀自己的情绪，人到中年，有了一定的时间一定的气韵，家也会随之幻化出多样色彩，集电影、卡拉ok、电视、音乐、游戏、吧台、舞池等功能于一体的多功能视听室，点缀芳华。</span>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 客户段先生是位事业非常成功的企业家，经常往来于欧洲各地，喜欢结交五湖四海的朋友，特地找到者尼来定制私人影院,起因于段先生在一起玩哈雷的、家住保利\r\n垄上的朋友家里感受到了者尼私人影院带来的视听盛宴，心中的某些情绪瞬间就被激发，段先生当下就打电话找到者尼私人影院，明确地表示希望能拥有一间功能强\r\n大的影音室，休闲、娱乐、会友，乐享人生。值得一提的是，段先生是个超级球迷，四年一次的世界杯，今年他希望能在自家的家庭影院里和朋友们一起共享世界杯\r\n这专属球迷的节日。所以者尼的设计师要赶在世界杯开幕之前把这个功能强大的视听室做出来，而距离世界杯开幕只剩下一个多月的时间，这对者尼设计师而言是个\r\n不小的挑战。事实证明，者尼私人影院在快速响应客户方面完全经得起考验，虽然工期短、任务重，但是者尼人凭借着专业、专注的精神完全是保质保量地完成了这\r\n间功能强大的视听室。</span><br />\r\n<span style="font-size:14px;"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n首先要说的是这个视听间的环境，整个视听室的面积还是比较大的，长为11米，宽为6.3米，高为2.9米。对于视听室的整体色调，段先生非常信任者尼设计\r\n师的审美能力，具体的色调方面并没有给设计师很多限定，这就给了设计师很大的发挥的空间。理论上来讲，在欣赏视听节目时，需要较暗的光线，任何杂光都会影\r\n响视觉欣赏效果。如果打造投影模式的家庭影院，则最好是全黑的环境，这种环境下才能有效提高银幕的对比度，展现最佳视觉效果。经过设计师的反复推敲，最后\r\n将视听室整体色调定为暖色调，咖啡色和暗红色为主，结合了客户经常往来于欧洲各地的经历，最终设计师把整个影院的风格定位为欧式奢华。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516071716_43107.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 设计师根据专业的“学术派”要求，墙体用了咖啡色吸音软包和六根弧形立柱的设计，基于光线的考虑，弧形立柱选用了几何花纹咖啡色的壁纸，不影响投影效果之\r\n余更显贵气，给人一种温馨的视听感受。弧形立柱不仅起到了美观的效果，更重要的是可以通过立柱做一些声学处理。因为视听间的房间比较大，投影幕旁的两根半\r\n罗马柱的主要目的是吸收低频驻波，中间和后墙角落各自两根立柱则主要用来做声音的扩散。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516071739_82059.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 左侧墙体采用了咖啡色绒布软包处理，右侧有三扇窗，设计师选用了红色的波浪形窗帘，一是有助于吸音，再者有助于声音的扩散。墙顶做了两块星空顶营造出舒适\r\n浪漫的气氛。进出视听室的大门也是特别定制的软包双层隔音门，门边四周采用海绵密封胶条以隔绝声音的外泄。此外前面也提到了，工期较短，客户要求一个多月\r\n的时间完工，经过和客户的沟通没有对地面做太多的处理。因为硬砖块对声音的反射比较大，所以在视听室的前方放了20平米左右的地毯，一方面想把地毯覆盖的\r\n部分设计成舞池的效果，另一方面地毯能够吸收部分的声波。墙角做了舞台造型灯，举办舞会的时侯带动气氛。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516071831_89164.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 舞池</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516071858_89788.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 舞台造型灯</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516071919_26784.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 酒柜和吧台</span>\r\n</p>\r\n<p>\r\n	<br />\r\n<span style="font-size:14px;"></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 咖啡色靠背沙发、金色茶几、卷叶纹暗色墙纸、暗红色绒布落地窗帘、吧台、酒柜应有尽有，再配上两块星空顶，一切烘托出奢华雍容的气氛，但是颇有设计感的布置，完全没有暴发户那种想要炫耀的气场，而是将主人懂得享乐，乐于生活的精神表露无遗。<br />\r\n体会完这间多功能影音娱乐室所营造的良好的休闲气氛后，接下来我们就要看看其选用的设备是否能与这间影音室良好的气氛相得益彰。按照从音频到视频的顺序，我们先来说说对于音响设备的选择。\r\n	<p>\r\n		JBL SYNTHESIS至尊雅瑞 7.1声道系统（含功放）<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n美国JBL音响器材是世界著名的音响产品，闻名全球。目前已经由全球最顶尖的三大电影机构——美国华纳兄弟电影公司、环球影业以及迪士尼电影录音室所采\r\n用。也是众多THX专业影院设计工程师心目中的神话，业界唯一一套从音箱到功放均获得THX认证的多声道音响系统。同时，其产品的用家，每一个都是世界显\r\n赫有名的超级明星：《变形金刚》系列电影导演迈克尔•贝、国际著名歌星席琳•迪昂、好莱坞著名影星基努•里维斯、丹佐•华盛顿，以及包括老虎•伍兹在内的\r\n近百位世界各界名人共同钟爱并享用JBL SYNTHESIS的私家影院系统。JBL \r\nSYNTHESIS系统是专业产品民用化的代表，它以宽松自然的声音、澎湃的能量和强劲的动态而著称，一场席琳迪翁演唱会，现场环绕声演绎当时浓厚的现场\r\n气氛，人声清晰。再一场《阿凡达》，宏大的场面，震撼的音效已是让整个房间的气氛进入另一个世界。\r\n	</p>\r\n	<p>\r\n		<img src="/admin/hjd_Ub/attached/image/20120516/20120516071958_58262.jpg" alt="" />\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; JBL SYSTHESIS\r\n	</p>\r\n	<p>\r\n		Wharfedale 卡拉OK音箱 <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n再看看另一件强大的多功能产品—卡拉OK触摸屏，客厅摇身一变，成了练歌房，相信你一定会为这样专业的卡拉OK系统发出感叹，千万首歌，从现代到古典，从\r\n流行到民族，只要坐在沙发上轻轻触摸，周围就会弥漫起曼妙的音乐，在幕布的上方，设计师设计了两个独立的Wharfedale卡拉OK音箱，专供唱歌使\r\n用，让声音更加圆润富有质感。\r\n	</p>\r\n	<p>\r\n		<img src="/admin/hjd_Ub/attached/image/20120516/20120516072040_77381.jpg" alt="" />\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 卡拉OK音箱\r\n	</p>\r\n	<p>\r\n		SCREENPRO&nbsp; FXZ9165&nbsp; 165寸16：9画框幕布<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n幕面雕刻半球面微镜纹，表面光洁柔滑，可有效消除眩光现象，同时极大提高幕布的视场角及解像力，还原色彩的饱和度，令画面更生动、更清晰 \r\n。在视频调整方面因房间内颜色相对深色的搭配，故较容易发挥投影机与屏幕的性能，从而提高了画面的对比度，使画面的冲击力表现更为突出。在观看朗朗与波切\r\n利在意大利托斯卡纳（Tuscany）的演唱会上，画面不但呈现了郊野的辽阔，而且立体感层次表现突出，入夜后观众席上五彩缤纷的衣服和不同肤色的脸孔都\r\n能真实地呈现，可见SCREENPRO FXZ9165是一高性价比的利器。\r\n	</p>\r\n<img src="/admin/hjd_Ub/attached/image/20120516/20120516072116_28998.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 165寸16:9画框幕布</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">富可视&nbsp; SP8682&nbsp; 1080P高清高亮投影机<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n段先生在看过众多投影机之后，欧美风格的代表富可视SP8682带来的真实视感吸引了段先生的眼球。拥有全高清物理分辨率的SP8682外形方方正正更显\r\n大气稳重。与以往的DLP机型相比，SP8682拥有5000:1的原生对比度，配合1200流明的输出亮度、6段RGB色轮技术以及Brilliant\r\nColor色彩处理技术，能带来更为出色的画面色彩再现能力。优秀的画面重塑能力，色彩饱满，各色阶过渡均匀，层次感、立体感以及景深感都尤其突出。在实\r\n试《叶问》的打斗场面上，东北大汉用后脚撩起砍刀并顺手接过砍向叶问的一霎，冷酷的金属刀声配合流畅的画面尤如真得要将眼前的屏幕砍开一样，音效和速度都\r\n达到令人满意的效果。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516072144_49482.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 投影机</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">飞利浦 触摸控制屏 <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在这里，我们先看看段先生家的设备清单：一套JBL \r\nSYNTHESIS的音响系统，一台ZENE多媒体中心、一台富可视投影机、一台卡拉OK服务器，还有无数的灯光设备等，光是这些设备的遥控器就多达数\r\n件，在使用过程中难免显得繁乱复杂。但我们同时发现，在王先生的影音室中没有看到一台遥控器，只有一个触控设备。是的，这就是启动影音室所有设备的大脑，\r\n一台PHILIPS的智能中控，兼容房间中的一切遥控设备，包括空调、灯光一切可遥控的设备。中控还设置了六路智能照明控制系统，六种常用照明模式，客户\r\n可根据喜好调整相应模式，不同娱乐模式会自动切换到相应的灯光场景。有了它，就可以坐在柔软的沙发上触控影音室的所有设备，让你在影音室的一切都显得那么\r\n随意、舒适。\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n世界杯就仿佛席卷全球的热浪，让亿万球迷翘首以待的热情迅速点燃。在者尼私人影院中感受世界杯那令人疯狂痴迷的魔法，血脉贲张、热血沸腾，伴随迭荡起伏的\r\n赛场而疯狂呐喊。一出出美轮美奂、精彩绝伦，无需语言来诠释的大片正热情洋溢、激情奔放地在者尼私人影院上演着。\r\n	</p>\r\n<br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><br />\r\n</span>\r\n</p>', 0, '2012-05-16 15:22:10', 0, 0, '', '', '/pic/1337152586430.jpg', '/pic/1337152586631.jpg', '/pic/1337152586842.jpg');
INSERT INTO `essay` VALUES(52, 15, '享受今天', '/pic/1337153231317.jpg', 'pic/GD/13371532319171317.jpg', '<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 冬去春来，光阴荏苒，时光总是匆匆、太匆匆地就从我们身边悄悄地溜走，再忙、再累，也要学会欣赏人生路上那每一处值得留意的风景。当一丝春天的雨\r\n滴飘入心田；当一缕夏日的轻风沁人心脾；当一叶秋日落叶坠入眼底；当一束冬日暖阳射入心窗，你会发现，生活原本就这么美好！想起《浮士德》的那句名言：等\r\n待片刻，停留一会儿。</span> \r\n<p>\r\n	<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14px;"> 用户梁女士是一位投资公司老总，多年远飘海外，追求前卫与时尚，喜欢哈雷摩托，喜欢Volovo，喜欢在舒适的视听室happy \r\nKTV，和朋友一起分享好莱坞最新大片，打打最新的XBOX游戏，忙里偷闲，让心灵得到片刻的停留。者尼设计师根据客户需求将这个视听间打造成了一个集交\r\n友、娱乐、休闲于一体的多功能影音娱乐室，赋予了视听室更多的功能性和互动性，毕竟“独乐乐不如众乐乐”。</span><br />\r\n<span style="font-size:14px;"> 首先要说的是这个视听间的环境，房间实际大小25（6*4.5）平米左右，但是受到通风和管道的影响，改造后的使用面积只有20平米左右。另外房间原始高\r\n度只有2.6米，较低的层高给人压抑感觉的同时还不利于声音的扩散。为了提高整体声场的空间感，者尼设计师建议客户将层高增加到3.3米，唯一的解决方案\r\n就是往地下挖。最后挖地70公分，视听间的整体容积非常适合声音的还原与播放。为了增加视听室的情趣，者尼设计师还特别为客户在房间顶部设计了星空图，营\r\n造了浪漫星空的感觉。无论是在欣赏电影还是在KTV场景都可以轻松打开头顶上的那块星空……夜幕帷落，漫天的星星，带着你走出钢筋混凝土的森林，沉醉在这\r\n浪漫的夜空，撒下一地的幸福。连梁女士都不得不佩服这种设计着实是将科技、艺术、娱乐与丰富想象力的完美结合</span><span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;">。</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516072741_57647.jpg" alt="" /></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><span style="font-family:SimSun;font-size:14px;">&nbsp;&nbsp;&nbsp; 对于影院的整体色调，梁女士非常信任者尼设计师的审美能力，具体的色调方面并没有给设计师很多限定，只是希望自己所拥有的这间多功能的影音视听间能够达到\r\n专业标准。这就给了设计师很大的发挥的空间。理论上来讲，在欣赏视听节目时，需要较暗的光线，任何杂光都会影响视觉欣赏效果。如果打造投影模式的家庭影\r\n院，则最好是全黑的环境，这种环境下才能有效提高银幕的对比度，展现最佳视觉效果。经过设计师的反复推敲，最后将视听室整体色调定为冷色调，黑灰为主，设\r\n计师根据专业的“学术派”要求，尽量以灰色吸光绒布做底色，整体音箱则使用枣红色，搭配米黄色头等舱沙发，一方面兼顾整个房间的装修格调，另一方面还能保\r\n证屏幕的对比度。</span></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><span style="font-family:SimSun;font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516072817_80471.jpg" alt="" /></span></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><span style="font-family:SimSun;font-size:14px;">&nbsp;&nbsp;&nbsp; 体会完这间多功能影音娱乐室所营造的良好的休闲气氛后，接下来我们就要看看其选用的设备是否能与这间影音室良好的气氛相得益彰。按照从视频到音频的顺序，我们先来说说对于投影设备的选择。<br />\r\n投影幕 加拿大美视 120寸 16:9弧形<br />\r\n&nbsp;&nbsp;&nbsp; 投影幕则采用16：9 \r\n弧形嵌墙式设计，幕面刻有微型精细钻石纹，纹理细腻，质地光洁柔滑，有效消除光波纹和水波纹的同时，能完美还原色彩的饱和度；视觉广，投影锐度均匀，无论\r\n欣赏者坐在哪个角度，都能看到同样清晰、真实的画面。纯色的幕面基调，表现明暗层次丰富细腻，对比度鲜明而不刺眼，质感十足，通透感出色过人。欣赏到完美\r\n无暇的超大画面视觉震撼的同时，让人身临其境，畅快淋漓。完美之影，嬉游间沉浸于光芒流转。</span></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><span style="font-family:SimSun;font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516072840_19107.jpg" alt="" /></span></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:华文中宋;font-size:14px;"><span style="font-family:SimSun;font-size:14px;"> </span></span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">富可视 X10&nbsp; 高亮度1080P影院投影机</span><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14px;"> 想追求完美的影院级效果来欣赏好莱坞大片，一部性能强劲的影院级投影机是必不可少的。有了它们，在家看大片才真正可以称得上是享受。富可视\r\nX10的外壳属磨砂质感，可以避免投影沾染指纹，投影机的镜头位于投影机的左侧，接口则完全设计在机身后部，另外兼顾防尘和美观的要求，还提供了一个接口\r\n挡板，可以遮盖繁杂的线缆。性能方面，富可视X10投影机采用了TI公司1080P全高清DLP芯片，内置极致色彩技术，对比度7500：1。为了提高色\r\n彩表现，富可视X10使用经过优化的7段色轮，使富可视X10的效果非常出色。此外，内置了Pixelwork \r\nDNX影像处理芯片，它不仅可以增强动态画面的效果，更能对静态影像进行优化。无论是展示清晨的朝霞、湛蓝的天空、暗夜的战争，还是表现人物细微的表情，\r\n都能够在你眼前重现电影大师们心中最完美的画面。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 对于欣赏大片、畅玩游戏来说，完美的影像同样需要强大的音响系统来支持。者尼设计师为客户搭配的是AUDIO CENTER \r\nIS10卡拉OK音箱、安桥 906功放以及REVEL扬声器，这套音响系统的搭配足以将声音的临场感觉表达得淋漓尽致。一首金属乐队的“sad but\r\ntrue”，鼓手坚实有力的擂击，具有很强的冲击力，可谓拳拳到肉，每一个鼓的定位相当准确，弱音细节丰富，鼓皮与鼓棒接触瞬间的摩擦感也很明显，空气的\r\n残响和气流迫振效应都能真实地还原出来。观看好莱坞大片时更是效果惊人——播放影片的时候它们几乎是“纤毫必现”地将所有的影片信息传达给听众，那种致密\r\n丰富的声音信息量让人吃惊，所有的细节，隐藏在声轨中的小瑕疵都被完全展示了出来，那种饱含能量的听觉刺激不断地从四面八方包围而来有，恍如置身于电影院。</span> \r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073011_66997.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span><span style="font-family:华文中宋;font-size:10.5pt;"></span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14px;">梁女士对于者尼设计师给予的设计方案非常满意，于是把整个房间的冷热风控制、有线电视、网络，全交给者尼的设计师来处理。当然了，者尼设计师也是不负众望，\r\n为用户设计了一个整体专业级机柜，将所有信号源、音视频设备、网络设备、KTV设备、时序净化电源全部有序地融合在了一起。确保了电源干净，声音无杂波。\r\n当然了，作为一套高端的家庭影院，智能家居系统也是必不可少的，毕竟很少有人愿意在看电影之前依次打开投影机、音箱、播放器等器材。只需一套智能家居系\r\n统，用户可以通过智能遥控器进行控制，轻启几个按键，一切就可以准备就绪。安装了这套智能影音系统以后，用户坐在沙发上无需起身，轻轻点击一下按键，应该\r\n打开的设备全部依次打开，所有需要关闭的设备全部依次关闭，便捷的同时也增加了使用的安全系数……一切繁杂操作全都由系统代劳。</span> \r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073038_37916.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> Be still……Just for a moment……</span><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14px;"> 片刻停留，聆听一份纯净，寻觅一份情怀。忙里偷闲，偶尔停下匆忙的脚步，煮上一杯暖暖的咖啡，将咖啡杯放在柔柔的灯光下，让袅袅的咖啡香气一点一点氤氲开\r\n来。看上一部最新的好莱坞大片，玩一款最新的XBOX游戏，选上一曲心仪的音乐，倾听音符与音符温暖明亮地在空中绽放，做短暂停留，停留的刹那，倾听内心\r\n喜悦盛放的轻响。</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>', 0, '2012-05-16 15:32:54', 0, 0, '', '', '/pic/1337153231345.jpg', '/pic/1337153231268.jpg', '/pic/1337153231541.jpg');
INSERT INTO `essay` VALUES(53, 15, '声色盛宴', '/pic/1337153789568.jpg', 'pic/GD/13371537896199568.jpg', '<p>\r\n	<span style="font-size:14px;"> 客厅是姚先生的会客中心，充分体现出姚先生对生活的追求。影视墙采用了电影院的风格，在幕布的周围，挂起了深色幕帘，在体现大方的同时，又可以对幕布做一\r\n个很好的保护作用，周围马赛克和红枫木的独特工艺，让视听间更加气派奢华，彰显主人的热情和对生活的热望。每一个设计都做到严谨，连一个小小的线头都不能\r\n例外，不能因为任何一个小小的败笔，而影响到整体的完美。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516073659_34032.jpg" alt="" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 灯光对于私人影院的气氛营造有着非常大的作用，因此也要多下些功夫，暖色调可以使人久坐不疲，舒适而不张扬，沏一杯咖啡，会几个朋友，看一部电影，都会感\r\n觉像在另一个世界，没有喧哗，没有吵闹，更没有闲杂琐事，只是单纯的享受生活，让时间过得更具格调。幕布上方的黄色系照明温馨而不刺眼，加上射灯的强烈效\r\n果，让视觉更加明亮，不会造成喧宾夺主的感觉。让冬天的视听间更加神秘，夏天的视听间更加璀璨。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516073717_56271.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 让视听来的更猛烈一些</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 也许是对音响的极度发烧热爱，对音质的苛刻追求让姚先生不惜重金选择了震撼的7.2声道设计，两只落地低音炮的伫立让人热血涌动，仿佛那震撼的音效已在血\r\n液中沸腾。ZENE为姚先生选用的是revel“指挥家”系列音箱，声音的爆破效果极强，音质敦厚，更具震撼力。比起“JBL”的柔美与“金榜”的清纯，\r\n显然revel“指挥家”更适合姚先生狂热的风格。而且姚先生喜欢hip-hop的音乐，高音频和低音频的区分都很明显，所以低音炮的选用就很重要，两个\r\n低音炮让低音更加充分，达到极致，轻松演绎主人的要求。当然，要想使这套震撼的音响组合推到完美极致，也需要配备适合的功放，就像一部好的车子需要强劲的\r\n发动机，ZENE为姚先生配备了强劲的lexicon前后级功率放大器，相信大家对于lexicon的功放并不陌生, \r\n世界上80%的音乐公司使用Lexicon的设备进行录音和混音。在顶级汽车品牌如劳斯莱斯幻影，奔驰C级都可以看到lexicon的身影。比起天龙与安\r\n桥系列功放设备，推动revel“指挥家”系列音箱更加符合主人的要求。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我们现场测试了几部电影，《LORD OF THE \r\nRINGS》一开场甘道夫与神秘火使者打斗的场面营造出一阵阵的低频，已感到lexicon功放与revel音箱的蓄势待发。最后决战的一幕，人类和魔族\r\n激战的声音前后明晰清楚，环绕效果出色。人魔交锋的宏伟画面，奔驰的马蹄声，惊险打斗的音量感十分惊人，力度充沛，距离如临其境；《英雄》片段音效用了\r\nDTS-ES，播放梁朝伟与李连杰对剑一幕，配乐声响本已很呛耳，但两剑接触瞬间又尖又刺耳的金属声音很有质地，音效逼真。大兵压近包围书院的时候，澎湃\r\n的声压近在眼前，极有气势，一步步移动的步伐天摇地动效果很有临场魄力，，现在我们听到的脚步声以及马蹄声地动山摇，但并没有掩盖屋内利箭击落木碎掉在地\r\n上的声音，强弱对比都能同时兼顾特别是弓插入沙堆那股咔嚓声，带有层次的低频叫姚先生怎能不选择呢？</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 音箱的摆放位置也很重要，不能太靠墙，以免吸音过于强烈，爆破的效果受到影响，这样功放的效果再好也会大打折扣，音箱与音箱之间要有合适的位置，做到既独立又融合，既有主次之分又倍显和谐。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073804_24872.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 随着奔放的音乐响起，我们和姚先生达成了共鸣，进入了hip-hop的世界，从心底引发的震撼力，让全身都想跟随音乐摆动起来，姚先生放的音乐确实很好\r\n听，有富有动感激情的摇滚，有富有诱惑力的现代歌曲，还有高贵典雅的古典音乐，音乐也体现一个人的情感，能看出姚先生是一个情感丰富的人，能够充满激情，\r\n也能够细腻入微。姚先生还喜欢动作电影，浓烈的色彩充斥着整个视角，配合侧墙上的环绕，让视听感觉立体起来，仿佛身临其境，画面在浓郁中更加浓郁，声音在\r\n强烈中更加坚韧。对于真正会享受生活的人来说，器材只是一种回放设备，只要音乐能触动心弦，电影能震撼人心，适合自己，就是最好的。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073830_99785.jpg" alt="" /><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-size:14px;">现代高科技智能产品充斥着我们的视野，全智能室内中控设备与私人影院完美融合，只需几个按钮，就可以控制影院内所有灯光及电子设备，当暖昧的灯光随着音乐\r\n的节奏起舞时，那光与影的鬼魅是不是让你神往。平时酷爱摄影的姚先生对画面色彩相当挑剔，除了平时欣赏电影，姚先生更愿意将自己的作品在大屏幕上与家人朋\r\n友一同分享。投影机的选择也就尤为重要，画面的层次感更重要的是要通过投影表达。姚先生偏向于更深沉一点的色彩，不要太亮，但要很有层次感，配合房间的深\r\n色格调，所以选择了富可视的IN83投影机，比起奥图码的HD80以及SONY的VW200更加符合主人的要求，做到画面不失真，浓烈中透出鲜明的层次，\r\n夸张中流露舒适，让视觉效果更加自然，达到姚先生对画面的色彩要求。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073853_45263.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 嵌入式的设备架设计既美观又节省空间，配合金属色边框，能够彻底把目光锁定在设计和环境，色彩的搭配上，而忽略了设备的存在。</span>\r\n</p>\r\n<p>\r\n	<img src="/admin/hjd_Ub/attached/image/20120516/20120516073916_86519.jpg" alt="" /> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 对于整套设备的灵魂，也是视听间的桥梁，就是ZENE的媒体中心了。只有媒体中心，才能将所有的一切融为一体，化为一种完美的个人化美丽。姚先生对生活的\r\n品质要求较高，当然对于经营赛车俱乐部的他，更注重画面的流畅及功能性，ZENE为姚先生选用了高端的EC-88V2媒体中心，比起ZENE的其他型号的\r\n媒体中心，这款功能更强大的指挥中心，集高清视频、蓝光DVD、游戏，3D游戏、卡拉OK、数字音频播放，图片-幻灯片播放，真彩电视、实时录制，时光平\r\n移、电视指南、DVD刻录等功能于一体，带有1080触摸屏的EC-88V2媒体中心，让姚先生不开投影即可感受音乐的无处不在，方便快捷。而“极品飞\r\n车”游戏让姚先生玩的乐此不疲，配合全智能中控设备，使整个视听间的操作变得简单起来，享受前所未有的视听效果。全新的家庭娱乐体验方式，让生活多一分快\r\n乐，让人生多一分享受。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516073938_31437.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 在视听间的后墙上，我们看到设计了入墙式的后环绕和透声布，是声音扩散，做到声音不刺耳，想想如果从后面发出震耳欲聋的声音，是不是会有些不舒服呢？透声\r\n布的使用，不仅让听觉效果更加完美，还可以遮盖音箱，让视听间更加简洁，为了整个视听间的美学设计，连一个小小的环绕音箱也不能忽视，将投影机设计在中\r\n央，是再合适不过了，简单大方，没有多余的繁文缛节，也体现了姚先生的生活方式，简单中带有奢华，舒适中富于品味。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516073959_86434.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> 要做到专业的视听间，吸音是必须处理的，否则一切的设备都会变成摆设，效果会大打折扣，不做吸音处理，声音会变的浑浊不清晰，稳定性极差，吸音可以在很多\r\n地方做文章，可以做入墙式的吸音木板，布艺沙发，天花板嵌入吸音棉，可以使整块的，也可以是三维立体的，甚至一些布艺小摆设，也可以做到吸音效果，但不论\r\n怎么做吸音处理，都要保证视听间的完整性和美观性，要与整体色调保持一致。&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; \r\n吸音也要有“度”，有的私人视听间做吸音之后，声音会变得特别干燥，是因为隔音效果不太好，其他房间的噪音干扰视听间的效果，这样的环境我们会建议采取强\r\n吸音环境来降噪。而对于开放式的视听间，声压是很难构建的，所以在吸音材料上，就要有很专业的选择，要选用空腔吸音的材料，空腔越厚，吸音效果越好，一般\r\n卧室的隔音软包，更倾向于选择透气，密度高，防火的绒面料，而对于视听间，更应偏向于3D扩散体和整快的吸音材料，使紧凑，不拖泥带水,使得每个位置的音\r\n频听感都非常清晰。配合姚先生这间视听间的色彩，设计师采用了整块吸音软包，间隔雕花式的红枫木吸音板，吸音处理和整体风格完美融合。纯正的红木色，深蓝\r\n色吸音软包，倍显高贵典雅，加上墙面上方的红木雕花和射灯，起到画龙点睛的作用，使视听间奢华又不失一分生动，姚先生的客厅无论从色调的搭配还是视听的搭\r\n配都是协调的，舒适奢华的客厅配上一首心仪的歌曲或一部动情的电影，我想不管有多少疲惫也会马上悄然逝去了吧.</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516074020_16270.jpg" alt="" /></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"> </span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 品质生活，奢华打造</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 生棕色皮沙发，周围的暖光射灯，方格式木质天花板间隔五彩壁纸是不是会让人眼前一亮，发出完美的感叹呢？灰白色的地毯在吸音的同时，和整个视听间的深色调\r\n形成鲜明对比，柔和地提亮了色彩，使视听间在高贵中显露活泼，严肃中不失舒适。圆形的台桌更是对比出简单大方线条清晰的真皮沙发，深沉中富有质感，客厅是\r\n休息的地方，在忙碌了一天之后，坐在舒适的沙发上，聆听喜欢的音乐，观赏热爱的电影，是一件多么惬意的事啊！</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 姚先生的视听间锁住了我们的目光，阐述了我们的向往，但锁不住我们的视听。优雅的音乐，美丽的景色遍布整个城市，当我们完成了一天的工作之后，也给自己留\r\n一些空间，把耳朵叫醒，静静地追寻过往的岁月，在这个繁华的大都市背后，享受属于你的权利，聆听那份属于自己的声音，专注于这个城市的每一个角落，感受最\r\n真实的“私人”视觉盛宴。</span>\r\n</p>\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 寻它千百度，其实无处不在。</span><br />\r\n<p>\r\n	<br />\r\n</p>', 0, '2012-05-16 15:42:13', 0, 0, '', '', '/pic/1337153789110.jpg', '/pic/1337153789720.jpg', '/pic/1337153789751.jpg');
INSERT INTO `essay` VALUES(54, 15, '古朴现代话别喧嚣', '/pic/1337154711849.jpg', 'pic/GD/13371547115921849.jpg', '<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 生活像本书，越看越薄，而厚度越来越深。在这不断加深的厚度中，为了理想，有很多让人不得不放弃的东西。洗尽铅华的背后，是那颗随着岁月渐渐淡定的心情。总有一片天空可以找回那份曾经遗失的美好。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516074646_47457.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 走进江先生的家，第一感觉古香古色，第二感觉男性化十足并且充满现代气息的设计，第三感觉宽敞。不难看出江先生将自己的客厅作为影音娱乐中心。大面积的木\r\n制家具装饰奠定了空间的主基调，黑色简约款式的真皮沙发让空间显得稳重和厚重起来，水晶吊灯提高了空间的亮度起到画龙点睛的效果，简洁中渗透出不对衬的美\r\n感。</span><br />\r\n<br />\r\n<span style="font-size:14px;"> 古香古色客厅做家庭影院</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 客厅作为接待客人的场所比视听间最佳状态相差甚远，但是由于我国的国情和风格所限，把会客厅和影音间合为一体。为了让客厅的环境更加的合适做家庭影院，我们要采取相应的装饰来弥补不是专业影音间的遗憾。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 江先生家的客厅给人感觉很宽敞，大面积的使用木质家具，这样不但美观有古朴的感觉，对吸音的处理也有很好的效果。客厅壁柜错落有致，不但可以储存东西也可\r\n以改善声音。沙发也是打造影音间的重要角色，沙发尽量选用布艺的沙发，如果说沙发是打造影音间的重要角色，那么影音间的地板、窗帘也是不容忽视的角色。地\r\n板最好选用软木地板，窗帘要选择厚重的窗帘，这样能做到很好的吸音效果。墙上最好装些布衣的装饰品，不仅增添室内的情趣，还能很好的吸收杂音。 </span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 落地窗是音效的最大克星，但是不用为这个挠头，落地窗可以选厚重的玻璃加工牢固后，在窗前挂上厚重的窗帘，这就是我们说的，能起到很好的吸音效果。另外客\r\n厅的门也要加厚加重。此外天花板的隔音处理也很重要，可以在天花板上安装轻质的石膏板，或者用专业隔音材料做个隔音吊顶，这样不但能隔音，还能起到美观作\r\n用。</span></span><span style="font-size:14px;"><br />\r\n&nbsp; <img src="/admin/hjd_Ub/attached/image/20120516/20120516074733_24405.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 暖暖灯光丝丝惬意</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 家庭影院中，特别是使用投影机的时候，光线环境对画质影响也很大，所以光线也要处理的恰到好处。光线处理分为内线光和外线光。通俗一点说就是，室外光，电\r\n灯和室内光。一般室外光，用深色厚重的窗帘就能解决；室内光尽量采用柔和光，打造出温馨的感觉，灯光不要直射屏幕上，最好不要使用白光灯，以避免造成噪音\r\n的困扰，室内的色调尽量靠近冷色系，尽可能的减少白色的家具布置黑色为最佳。从案例图上看，江先生家的白色家具布置少之又少。这样能够让人在视觉上感到明\r\n亮宽敞，清新。另外特别注意的是，家具的色彩、材质尽量统一，否则会造成视觉上的压迫感。从江先生家的案例看，江先生家采用统一的木制家具，也采用统一的\r\n色系。空间整体感觉宽敞落落大方不失雅气。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516074821_25666.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一面豪放派的电视墙</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 每个人都有一面以自己为主题的电视墙，而这面电视墙的风格和主人公的性格有着直接的关系。电视墙的种类很多，选择什么墙的都有。而江先生家的这面电视墙大\r\n有李白“君不见黄河之水天上来”的风范，所以我们给这面墙定义为豪放派的墙。电视墙采用镂空的文化石镶嵌，传统的花纹与幕布遥相呼应，文化石的吸音效果很\r\n不错，同时也能避免对其他居室的干扰，而且也可以烘托出媒体中心，功放等电器的金属质感，形成一种强烈的金属质感对比，也显得十分有现代的气息。</span></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516074922_13858.jpg" alt="" /></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电视墙前后有文章</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电视墙的主色调可用橙色、天蓝色等亮丽的色彩，多用两种对比强的色彩可以增强室内的气氛和视觉冲击感受，色彩对比强的可以显得室内就有现代活泼的气息。墙\r\n壁上尽量装饰些冷色的节能灯，傍晚不论是看电影还是K歌，都可以营造出一种浪漫低调的氛围。壁灯的照射光不要太强，且光线要避免直射幕布，音响和人的脸\r\n部。在看电影时有柔和的反射光照明就可以了。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 电视墙也要有吸音的处理，而地板就是电视墙做吸音处理的最好主力，当电视墙吸音不足时，地板吸音比较均衡的特性就显示出来了。特别提醒，在电视墙周围，可\r\n以悬挂些吸音物品，如棉、麻、毛、丝类的纤维织品 ，壁挂也可以。不要小看这些小东西，在吸音效果上他们起到了很大的作用。</span>&nbsp; <br />\r\n</span></span>\r\n</p>\r\n<p>\r\n	<span><span><img src="/admin/hjd_Ub/attached/image/20120516/20120516074958_39369.jpg" alt="" /></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 客厅里简单的遥控设备</span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 参观完江先生家的卧室后，江先生带我们去了他的第二个客厅。走进第二个客厅后，给我们的感觉截然不同。如果说第一个客厅具有十足的男性气息的阳刚美，那么这客厅可谓女性味十足。洁净中带着大方，设计风格也是峰回路转，完全看不出是出自一人之手。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 选择什么风格，选用什么材质，都是让人挠头却又不得不考虑的问题。怎样又能让自己的视听间个性十足呢？谈起江先生的心得，江先生说：“知道自己想要什么，\r\n什么是重要的，什么是次要的。其次就是布置的先后顺序。最重要的就是电视墙的概念，所有的装饰都是围绕着它来展开，这样一来看似复杂的东西也就简单了。”</span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516075050_99368.jpg" alt="" /></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 布艺感增强的第二客厅</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 柔软的布料总是给人十分温馨的感觉，而且布料也可以吸收杂音，让室内更加的安静，同时还有很多的安全感。客厅采用布艺来装饰可以随着季节和心情来选择自己喜欢的颜色和图案。让整个房间大变样，并且使用性也很大。建议选用房间面积小的效果更佳。</span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516075129_89977.jpg" alt="" /></span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 家庭影院搭配也需要合适的空间</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 家庭影院空间小，那最好就不要用“背投”。选用壁挂电视就足够了，音响的大小也要适中，整体风格要与房间协调。要注意的是，音响不要和墙壁太靠近，以避免\r\n声音的削弱和回音的出现。背景墙也要尽量的布置简单，天花板的图案不要太花哨，要采用统一的色系或是格调。这样不会显得空间杂乱无章。墙壁如果可以，就选\r\n择折射板。当然也可以采用一些布艺的小挂件。不但给空间增添温馨效果，还能更好的做吸音处理。</span></span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;"><br />\r\n</span></span>\r\n</p>', 0, '2012-05-16 15:51:51', 0, 0, '', '', '/pic/1337154711170.jpg', '/pic/1337154711408.jpg', '/pic/1337154711283.jpg');
INSERT INTO `essay` VALUES(55, 15, '爱 重现', '/pic/1337155568458.jpg', 'pic/GD/13371555681858458.jpg', '<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本案例是ZENE公司为上海市中心一间高档私人别墅高级定制的私人影院，它的经典不仅在于它是ZENE私人影院设计力量的体现，还在于它圆了一对相爱人的\r\n梦，夫妻二人非常想留住他们相遇时的美丽，希望私人影院能体现出当年他们相识于美国时的那间老旧美式影院的风格，在已经流逝的岁月里，能够永远承载他们相\r\n识的缘分和永恒的幸福。</span><br />\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 这是一套老式洋房，没有地下室，主人将一楼的一间18平米的书房打造成私人影院，希望在闲暇之余和爱人一起坐下来，看一部美国大片或一些喜爱的电视剧，追述他们当年的激情。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516075507_66634.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主扬声器和超低音扬声器置于透声幕后方，这样既可以节省空间，又倍显专业，让高清视觉效果和立体听觉效果达到完美的融合，入墙式的设计配合蓝色部分的低频\r\n板共振吸音体和百灵达均衡器的使用，有效降低了小房间低频驻波的影响，低频干净、紧凑，不拖泥带水,使得每个位置的低频听感都非常清晰。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516075537_24166.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 侧墙上整块的吸音材料及声学3D扩散体，保证各个角度的吸音效果及均衡，更加有效的将分散的声音聚合起来，不仅让声音变得更具质感，还对线路做到很好的绝缘作用。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在环绕扬声器周围密布的多种方格式声学3D扩散体，对扬声器的作用进行了很好的补充，加强环绕声场的声音扩散，使得震撼效果，轻松得到。</span></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516075611_79326.jpg" alt="" /></span></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 将弧形扩散体和吸音体布置在天花板隔间，使整个房间的听觉效果更加圆润，清新，不刺耳，不暴躁。智能中控的使用，达到了真正的所有娱乐一手掌控。 \r\n当用透声布覆盖里面的一切之后，所有的声学处理材料和扬声器都“消失”了，追求完美的设计让一个小小的空调，甚至一个电线头也不能例外，眼前呈现的，只是\r\n一间简单，大方的访旧美式视听间。</span></span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516080303_31821.jpg" alt="" /></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 深蓝和黑色是老旧美式影院的基本色调，配合米白色的地毯，视听间倍显高雅，暖光设计，更是不失奢华色彩，简约的形式，大方的线条，深蓝色条面设计，扁宽式台阶，黑色简易沙发，无不配合老旧的美感，追忆的色彩无处不在，没有多余的繁文缛节，体现出主人超凡脱俗的生活追求。 </span><br />\r\n<span style="font-size:14px;"> &nbsp;&nbsp; </span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 这套“爱•重现”的经典案例充分说明用高品质的视听器材堆积起来的不一定就是最好的，一个真正好的私人影院首当其冲的应该是设计，视听器材只是实现设计必\r\n备的工具而已。ZENE的每一道工序都符合THX“每个位置都是皇帝位”的标准。现代高科技力量配合古典风格，动感逼真的视听享受，ZENE作为这个行业\r\n的先行者，致力于打造国内高品味、高品质的私人影院，相信这套“爱•重现”会实现这对夫妇的人生之美，让ZENE贴心的服务长伴他们的生活，让他们的“美\r\n国梦”成为他们人生长路上的一条风景线。</span></span></span>\r\n</p>', 0, '2012-05-16 16:06:08', 0, 0, '', '', '/pic/1337155568510.jpg', '/pic/1337155568773.jpg', '/pic/1337155568948.jpg');
INSERT INTO `essay` VALUES(56, 15, '家的感觉', '/pic/1337156045351.jpg', 'pic/GD/13371560457185351.jpg', '&nbsp;\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成功，不在于拥有更多财富，而在于拥有更多与家人分享快乐的时光；品味，不在于浮夸纷繁的表象，而在于真正尊重人性的美妙设计；坚持，不在于刻意固守原点，而在于既重视家庭影院的外表而又决不向徒有其表妥协。</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081032_50421.jpg" alt="" /></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 成功、品味、坚持，这三者看起来似乎并无关系但实际上又相辅相成，执着造就成功、成功铸就品味、品味支撑执着。对于居住于观塘别墅的毛女士来说，她就希望\r\n拥有一套集成功、品味、执着于一体的家庭影院——她心中的成功，不在于拥有更多财富，而是拥有更多与家人分享快乐的时光；她的品味，不在于浮夸纷繁的表\r\n象，而在于真正尊重人性的美妙设计；她的坚持，不在于刻意固守原点，而是既重视家庭影院的外表而又决不向徒有其表妥协。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在这样的状态下，我们为毛女士打造了简约装修与明朗色彩的视听室。简约式家庭影院设计其实是人的一种需要，纷繁复杂的家庭影院布置会使人的视觉神经长期处于一种紧张的状态，而简约式的影院装修设计带给人的正好是宁静，休闲的感觉，这正是毛女士想要的家的感觉。</span></span>\r\n</p>\r\n<p>\r\n	<span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081103_30000.jpg" alt="" /></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 家是要顾及别人感受的场所，这里的别人也包括了邻居。远亲不如近邻，但如果经常让邻居听到自己家的大炮声，那么可能你很快会成为“小区公敌”了。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 基于以上的考虑，毛女士对视听室的装修设计人员提出的首要要求是不管家里声音有多大，绝对不能让一丝声音泄漏出去。设计方也认识到隔音其实是一个重要的问\r\n题，于是下了一些苦工夫。首先是墙的处理，毛女士的别墅是新房装修，于是设计师采用了墙表面做木质龙骨并塞满隔音棉，最后附上多层石膏板的方案。石膏板间\r\n还可以夹上隔音胶垫钉死，以这种多层隔离并吸收的方式来达到最好的隔音效果。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 除了墙体，门的隔音也很重要，哪怕处理得再完美的墙遇到一扇“漏声”门，那么就功亏一篑了。门的处理比墙体复杂一点，门沿是漏声最严重的地方，所以必须使\r\n用大量橡胶条保证门与门套间紧密连接以实现密封隔音。毛女士的门是在装修现场用实木制作的中空门，并在内部填充吸音棉，外面包裹吸音毡。这样可以达到隔音\r\n与吸音的双重效果。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 除了隔音，环境中必要的吸音与扩音也是必不可少的，因为“重视家庭影院的外表又决不向徒有其表妥协”也是毛女士对视听室的坚持。天花板是视听室装修中容易\r\n被忽视的地方，但实际却非常重要。我们都知道对于影院声的灵魂在于“对白”，重放好“对白”才能让人在120分钟的时间之内牢牢地“钉”在沙发上。而想要\r\n获得鲜活如飘在空气中的人声，那天花板的扩散就必不可少。于是，设计师在天花板与两侧墙面前端都安装了扩散板。除此之外，聆听位置的后墙面也使用了较多的\r\n“扩散”材料，这是为了获得音乐场所空间感与规模感，获得自然而又松融飘逸的高频表现，并加强后方的弱音重现能力。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; “视听室的调整特别需要注意声音的均衡性，无论是你在前排座椅还是后排座椅，听到的声音没有太大的变化，那才是合格的视听室。</span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081202_17498.jpg" alt="" /></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 全黑的环境可以让投影机发挥出最高的水平，并且观看者也不会因为周边环境的变化而分心，全情投入于情节之中。但事事无绝对，有一些人就不喜欢全黑的环境，\r\n他们认为这是一种压力。可能是基于这种原因，毛女士有一个习惯——她与家人观看电影时，喜欢开一盏小灯，保持家里有柔和的环境光。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 要把这个习惯延续到视听室环境中，就必须注意幕布的选择。白幕最能表现出投影机的原汁原味的画面，但让其良好发挥的前提是消除一切室内光线，保持黑暗的观\r\n看环境。这是由于白幕的反射能力强，如果有除投影机之外的其地光亮出现，画面对比度会明显下降。毛女士最后使用了美视大纯平的画框灰幕。灰幕增益比白幕\r\n低，可以削弱周围环境光对画面的影响，还可以使投影画面的黑色看起来更加深沉纯正。</span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081235_50987.jpg" alt="" /></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 家是没有压力的地方，更宽广的视听区域也有让人更加放松的作用。所以，毛女士并没有将视听室收缩为最常见的20~30㎡，而坚持打造了这间40㎡的家庭影院。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 大空间对器材的选择也就更加弹性灵活，视频器材上毛女士使用了富可视IN81投影机与美视120英寸画框幕，并以ZENE \r\nEC－58V2媒体中心作为播放源。这一组合的特点在于可以重现出色彩艳丽饱满，层次感都丰富的画面。观看最新的《冰河世纪3》，我们可以清楚地体会到\r\nInFocus投影机的优势——动物的毛发，人物的皮肤，金属的质感，暗部细节表现都非常突出。在黄鼠狼大战巨龙的片断，黑色层次依然毫不含糊。</span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081310_90963.jpg" alt="" /></span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 大空间的音箱与功放是没有办法省钱的，我们向毛女士推荐了REVEL演奏家系列音箱与天龙AVC—2808功放。天龙AVC—2808虽然并不算非常高端\r\n的功放，但它胜在拥有极强的爆发力与低频控制能力，音色也非常纯净。搭配REVEL演奏家音箱，声音走向属于迅速反应派——重放快节奏的打击场面优秀，高\r\n频清晰明亮，低频下潜深、动态大。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 大空间如果只用作影院，那无疑是太浪费了，所以毛女士还在这里购置了一套卡拉OK系统，其中的主力就是马兰士的卡拉OK专用音箱及舒尔PG58无线话\r\n筒。60年大庆之后，红歌流行，毛女士就时常在这里重现经典的爱国歌曲。在优秀器材帮助下，毛女士说哪怕演唱《我爱你，塞北的雪》这样的高难度歌曲，也能\r\n毫不费力。</span><br />\r\n<span style="font-size:14px;"> ZENE媒体中心是整个家庭影院的灵魂，毛女士的媒体中心软件配置合理，非常人性化与智能化。除了1080p高清视频与大型3D游戏，这台媒体中心还扩展了机顶盒，可录制电视节目，非常适合非发烧友用户使用。</span></span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span><span style="font-size:14px;"><img src="/admin/hjd_Ub/attached/image/20120516/20120516081340_18915.jpg" alt="" /></span></span></span></span></span></span>\r\n</p>\r\n<p>\r\n	<span><span><span><span><span><span><span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 房子大，家电多，摇控器也多。在享受大片之前还有很多琐碎的工作要做。例如关窗帘，关灯，开投影机，拉开幕布，开音响等等。那么操控的便利与智能化这时就\r\n显得尤为重要。想象一下，无须起身，随手一按按钮，即满足你的任何需求。慵懒惬意地坐在舒适的试听椅上，明亮的灯光缓缓暗下，平整光洁的幕布徐徐降下，投\r\n影机自动开启，所有的AV器材同时打开。随着音乐慢慢响起，投影幕上便出现了一个高清画面。这一切都得依靠智能中控系统。智能中控系统最主要的目的就是为\r\n了减少遥控器，在专业的设备上进行编程后，将影院所需要到的产品串联起来，实现“一键操作”的目的，简化操作的繁琐过程。</span><br />\r\n<span style="font-size:14px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 智能家庭化带给现代消费者的高品质体验远远不只这些，它以住宅为平台，集系统，结构，控制于一体。包括智能影音系统，背景音乐系统，互动娱乐系统，安全防\r\n范系统等诸多解决方案。毛女士的智能家居系统现在已经拥有了非常多的功能，今后还可以随着使用需求的升级去增加其他功能。</span></span></span></span></span></span></span>\r\n</p>', 0, '2012-05-16 16:14:05', 0, 0, '', '', '/pic/1337156045851.jpg', '/pic/1337156045841.jpg', '/pic/1337156045383.jpg');
INSERT INTO `essay` VALUES(58, 18, '设计理念', '', '', '<p>\r\n	<span><span style="font-family:宋体;font-size:14px;">&nbsp;&nbsp;&nbsp; 者尼私人影院设计参考标准是美国</span><span style="font-size:14px;">CEDIA</span></span><span><span><span style="font-family:宋体;font-size:14px;">协会于</span><span style="font-size:14px;">2009</span></span></span><span><span><span style="font-family:宋体;font-size:14px;">年和</span><span style="font-size:14px;">2010</span></span></span><span><span><span style="font-family:宋体;font-size:14px;">年颁布的《</span><span style="font-size:14px;">CEA/CEDIA-CEB22 Home Theater Recommended Practice: Audio Design</span></span></span><span><span><span style="font-family:宋体;font-size:14px;">》和《</span><span style="font-size:14px;">CEA/CEDIA-CEB23\r\nHome Theater Video Design</span></span></span><span><span><span style="font-family:宋体;font-size:14px;">》两大行业标准。</span><span style="font-size:14px;">CEB22</span></span></span><span><span><span style="font-family:宋体;font-size:14px;">是家庭影院音质设计标准，</span><span style="font-size:14px;">CEB23</span></span></span><span style="font-size:14px;"><span style="font-size:14px;"><span style="font-family:宋体;font-size:14px;">是家庭影院画质设计标准。</span></span></span><span></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', 0, '2012-05-17 09:49:48', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(59, 20, '知识库', '', '', '<p>\r\n	<span style="font-size:24.0pt;font-family:;" "="">CEA Bulletin</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:18.0pt;font-family:;" "="">Home Theater\r\nRecommended</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:18.0pt;font-family:;" "="">Practice: Audio\r\nDesign</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:18.0pt;font-family:;" "="">CEA/CEDIA-CEB22</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">NOTICE</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">Consumer\r\nElectronics Association (CEA</span><span style="font-size:8.0pt;font-family:" color:black;"="">®</span><span style="font-size:12.0pt;font-family:" color:black;"="">)/Custom Electronic Design and Installation</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">Association (CEDIA</span><span style="font-size:8.0pt;font-family:" color:black;"="">®</span><span style="font-size:12.0pt;font-family:" color:black;"="">)Standards,\r\nBulletins and other technical publications are designed toserve the public\r\ninterest through eliminating misunderstandings between manufacturers\r\nandpurchasers, facilitating interchangeability and improvement of products, and\r\nassisting thepurchaser in selecting and obtaining with minimum delay the proper\r\nproduct for his particularneed. Existence of such Standards, Bulletins and\r\nother technical publications shall not in anyrespect preclude any member or\r\nnonmember of CEA or CEDIA from manufacturing or sellingproducts not conforming\r\nto such Standards, Bulletins or other technical publications, nor shall</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">the existence of\r\nsuch Standards, Bulletins and other technical publications preclude\r\ntheirvoluntary use by those other than CEA members, whether the bulletin is to\r\nbe used eitherdomestically or internationally.</span> \r\n</p>\r\n<p style="text-align:left;text-indent:24.0pt;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">Standards, Bulletins and other technical\r\npublications are adopted by CEA in accordance with theAmerican National\r\nStandards Institute (ANSI) patent policy. By such action, CEA does notassume\r\nany liability to any patent owner, nor does it assume any obligation whatever\r\nto partiesadopting the Standard, Bulletin or other technical publication.</span> \r\n</p>\r\n<p style="text-align:left;text-indent:24.0pt;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">This CEA/CEDIA Bulletin is considered to have\r\nInternational Standardization implication, butthe International\r\nElectrotechnical Commission activity has not progressed to the point where\r\navalid comparison between the CEA Bulletin and the IEC document can be made.</span> \r\n</p>\r\n<p style="text-align:left;text-indent:24.0pt;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">This Bulletin does not purport to address all\r\nsafety problems associated with its use or allapplicable regulatory\r\nrequirements. It is the responsibility of the user of this Bulletin to\r\nestablishappropriate safety and health practices and to determine the\r\napplicability of regulatorylimitations before its use.(Formulated under the\r\ncognizance of the CEA/CEDIA R10 Residential Systems Committee.)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"=""> </span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:" color:black;"="">PRICE:\r\nPlease call IHS, USA and Canada (1-800-854-7179)</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:" color:black;"="">International\r\n(303-397-7956), or</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:" color:blue;"="">http://global.ihs.com</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:12.0pt;font-family:" color:black;"="">or</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">The CEDIA Bookstore</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:#810081;"="">http://www.CEDIA.org/resources</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">All rights reserved</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12.0pt;font-family:" color:black;"="">Printed in U.S.A.</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12.0pt;font-family:" color:black;"=""> </span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:13.0pt;font-family:;" "="">Foreword</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">This guide outlines recommendations for the audio design of high\r\nperformance home theaters.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">CEDIA and its certified Electronic Systems Contractors (ESCs) follow these\r\npractices, where</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">applicable, to deliver high value to dedicated home theater environments,\r\nhelping ensure full</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">performance potential for the equipment and the room.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<i><span style="font-size:11.0pt;font-family:;" "="">Home Theater Audio\r\nDesign </span></i><span style="font-size:11.0pt;font-family:;" "="">provides a\r\nbaseline for the design and installation of residential</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">spaces to be used for home theater or multi-channel music playback. While\r\nthe focus of these twoapplications is home theater audio design, many of the\r\npractices are applicable to multi-purpose,two-channel and other acoustic\r\nspaces.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">CEDIA recommendations are assembled from its ESCs’ years of experience and\r\ncumulative</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">wisdom. Industry standards, practices and recommendations published by\r\nother relevant bodies,as well as commonly accepted commercial practices now\r\nregarded as “de facto standards” in theaudio industry, are also represented in\r\nthis document.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">Standards include those from:</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">Society of Motion\r\nPicture and Television Engineers (SMPTE)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">Audio Engineering\r\nSociety (AES)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">International\r\nTelecommunications Union (ITU)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">European\r\nBroadcasting Union (EBU)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">International\r\nElectrotechnical Commission (IEC)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">International\r\nOrganization for Standardization (ISO)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">American Society\r\nof Heating, Refrigeration and Air-conditioning Engineers (ASHRAE)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">Accepted commercial practices include those from:</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">Dolby\r\nLaboratories, Inc.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">THX, Ltd. (THX)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:SymbolMT;">• </span><span style="font-size:11.0pt;font-family:;" "="">Imaging Sciences\r\nFoundation (ISF)</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:8.0pt;font-family:;" "=""> </span></b> \r\n</p>\r\n<br />\r\n<p style="text-align:left;" align="left">\r\n	<b><span style="font-size:16.0pt;font-family:;" "="">Introductio</span></b> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">Home theater rooms are gathering spaces with one main purpose: watching\r\nmovies. These</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">rooms can also have other uses like listening to and performing music,\r\nentertaining, gaming, orsimply lounging. It is important that the professionals\r\nwho deliver the home theater experience beaware of the precise set of video and\r\naudio standards by which film content is created. </span><i><span style="font-size:11.0pt;font-family:;" "="">HomeTheater Audio Design </span></i><span style="font-size:11.0pt;font-family:;" "="">outlines the various design parameters relevant to the\r\nproper design of ahome theater that meets relevant industry guidelines.\r\nAdditionally, these practices ensure that ahome theater supports other media\r\nuses such as music, gaming, and broadcast TV.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">Whether used as a dedicated space or a multi-purpose space, a properly\r\nexecuted home theatershould be able to faithfully reproduce the picture and\r\nsound content. That’s because the filmdirector and the entire crew working\r\nalongside the director carefully craft the content to create anemotionally\r\nengaging story that can transport the audience to far away lands in far away\r\ntimes.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">The best way to ensure that an ESC’s projects are successful and that\r\ncustomers are satisfied issimply adhering to movie technical production\r\nstandards.</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">As is evident from the length of this document, when designing a home\r\ntheater, many factors</span> \r\n</p>\r\n<p style="text-align:left;" align="left">\r\n	<span style="font-size:11.0pt;font-family:;" "="">come into play. Several are interdependent and interactive, and the design\r\nprocess will invariablybe iterative. This means that every step will need to be\r\nrevisited and altered, continuing aroundthe circle of decision points multiple\r\ntimes until the work is finished. Some compromises areinevitable. For example,\r\nthe picture screen and front speakers will compete for space since theyneed to\r\nbe in the same location. Of the several ways to deal with that issue, some\r\nyield betteroverall results than others. Ultimately, the best sounding and best\r\nlooking home theater is the onewith the most intelligent set of compromises!</span> \r\n</p>\r\n<br />', 0, '2012-05-17 10:23:33', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(60, 19, '方案设计', '', '', '<p>\r\n	<img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020154_77077.jpg" width="733" height="518" />\r\n</p>\r\n<p align="center">\r\n	原始户型\r\n</p>\r\n<img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020310_71375.jpg" width="733" height="519" /> \r\n<p align="center">\r\n	完成户型\r\n</p>', 0, '2012-05-17 10:04:01', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(61, 19, '布线检查', '', '', '&nbsp;\r\n<p style="text-align:center;" align="center">\r\n	<b><span style="font-family:宋体;font-size:14pt;">布线检查</span></b><b><span style="font-size:14pt;"></span></b>\r\n</p>\r\n<p style="text-align:center;" align="center">\r\n	<b><span style="font-size:14pt;"></span></b>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span><span>1.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style="font-family:宋体;">影音室的投影机标线</span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span style="font-family:宋体;"></span><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020518_14416.jpg" /><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:0cm;margin-left:18pt;" align="center">\r\n	<span></span><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span><span>2.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style="font-family:宋体;">事无巨细，每根都亲手标好的连接线</span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span style="font-family:宋体;"></span><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020534_88285.jpg" /><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:0cm;margin-left:18pt;" align="center">\r\n	<span></span><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span><span>3.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style="font-family:宋体;">影音室的工整走线</span><span> Cabling</span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020547_42472.jpg" /><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:0cm;margin-left:18pt;" align="center">\r\n	<span></span><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span><span>4.<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span><span style="font-family:宋体;">全宅网络，高清管道穿线</span>\r\n</p>\r\n<p style="text-align:center;text-indent:-18pt;margin-left:18pt;" align="center">\r\n	<span style="font-family:宋体;"></span><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517020602_28468.jpg" /><span></span>\r\n</p>\r\n<p style="text-align:center;text-indent:0cm;margin-left:18pt;" align="center">\r\n	<span></span><span></span>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', 0, '2012-05-17 10:06:06', 0, 0, '', '', '', '', '');
INSERT INTO `essay` VALUES(62, 19, '安装', '', '', '<p style="text-indent:-18pt;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;mso-list:l0 level1 lfo1;" class="MsoListParagraph" align="center">\r\n	<span style="mso-fareast-font-family:Calibri;mso-fareast-theme-font:minor-latin;mso-bidi-font-family:Calibri;mso-bidi-theme-font:minor-latin;" lang="EN-US"><span style="mso-list:Ignore;"><span style="font:7pt ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span></span></span><span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">工地检查</span></span>\r\n</p>\r\n<p style="text-indent:-18pt;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;mso-list:l0 level1 lfo1;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;"></span><span lang="EN-US">&nbsp;&nbsp;&nbsp;&nbsp; <img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022653_47735.jpg" width="348" /><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">布线点位</span><span lang="EN-US"><span style="font-family:Calibri;">(Cabling)</span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span lang="EN-US"><span style="font-family:Calibri;"><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022710_46114.jpg" width="348" height="261" /><o:p></o:p></span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">墙面开洞</span><span lang="EN-US"><span style="font-family:Calibri;">(Wall Cut-Out Size)</span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span lang="EN-US"><span style="font-family:Calibri;"><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022801_48034.jpg" width="348" /><o:p></o:p></span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">墙体结构</span><span lang="EN-US"><span style="font-family:Calibri;">(Wall Structure)</span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span lang="EN-US"><span style="font-family:Calibri;"><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022842_19302.jpg" width="348" /><o:p></o:p></span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">室内装修等</span><span lang="EN-US"><span style="font-family:Calibri;">(Interior Decoration)</span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span lang="EN-US"><span style="font-family:Calibri;"><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022900_94943.jpg" width="348" /><o:p></o:p></span></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">不错过每一个细节！</span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;"></span><span lang="EN-US"><img alt="" src="/admin/hjd_Ub/attached/image/20120517/20120517022919_71913.jpg" width="348" /><o:p></o:p></span></span>\r\n</p>\r\n<p style="text-indent:0cm;margin:0cm 0cm 0pt 18pt;mso-char-indent-count:0;" class="MsoListParagraph" align="center">\r\n	<span style="font-size:small;"><span lang="EN-US"><span style="font-family:Calibri;">Don</span></span><span style="font-family:宋体;mso-ascii-font-family:Calibri;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-font-family:Calibri;mso-hansi-theme-font:minor-latin;">’</span><span lang="EN-US"><span style="font-family:Calibri;">t Miss Any Details!<o:p></o:p></span></span></span>\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', 0, '2012-05-17 10:29:38', 0, 0, '', '', '', '', '');
