INSERT INTO `product` VALUES(13, 3, '812BE', '美国', '2分频，4只8''低音，4只6.5''超薄中音，4铍高音矩阵，LCR入墙前置旗舰扬声器', '独家钕磁低音喇叭和马达驱动技术,电焊铝材外壳,以及专利AFDC（可调节频率分布电路）,美国手工打造,气密密封外壳,,适合用于左中右扩声,铍铜合金四高音喇叭可提供无懈可击的精确声场，能够确保极为宽广的收听范围，以及独一无二的听觉效果。', '临兵斗者/bandr/812BE.gif', 0, '');
INSERT INTO `product` VALUES(14, 3, '62FX', '美国', '2分频，1只6.5''低音单元，LCS入墙式扬声器', 'James独家的钕驱动，气密密封外壳，适合中型房间环绕或主音箱，定制喷色，美国手工打制以及专利AFDC可调节频率分布电路。', '临兵斗者/bandr/62FX.jpg', 0, '');
INSERT INTO `product` VALUES(15, 3, '63FX', '美国', '2分频，2只6.5''低音单元，LCR入墙式扬声器', 'James独家的钕驱动,气密密封外壳,适合中型房间左中右主音箱,美国手工打制以及专利AFDC可调节频率分布电路。', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(16, 3, '62ASX', '中国', '2分频，1只6.5''超薄技术低音单元，LCS入墙式扬声器', '2分频，1只6.5''超薄技术低音单元，LCS入墙式扬声器，James独家的钕驱动，气密密封外壳，相当适合中型房间的环绕音箱，定制喷色，美国手工打制。', '临兵斗者/bandr/62ASX.jpg', 0, '');
INSERT INTO `product` VALUES(17, 3, '62CS', '美国', '高低音分体式设计、高音角度可调、2分频，6.5英寸', 'James独家的钕驱动，气密密封外壳，航空铝材，可绘制平面金属面罩，美国手工打制，适合高级多房间音乐系统，嵌顶式环绕音箱。', '临兵斗者/bandr/62CS.gif', 0, '');
INSERT INTO `product` VALUES(18, 3, '1200PT', '美国', '12英寸双导管无源超低音扬声器', '此款适合隔墙安装，导管技术低音系统可以克服入墙箱体低频下潜不足从而需要多只低音的缺点，美国手工制造，黑色。', '临兵斗者/bandr/1200PT.gif', 0, '');
INSERT INTO `product` VALUES(19, 3, '1500PT', '美国', '15英寸双导管无源超低音扬声器', '此款适合隔墙安装，导管技术低音系统可以克服入墙箱体低频下潜不足从而需要多只低音的缺点，美国手工制造，黑色。', '临兵斗者/bandr/1500PT.jpg', 0, '');
INSERT INTO `product` VALUES(20, 3, 'SPL39LCR-AL', '美国', '等离子电视配套扬声器', '所有平板电视专用音箱都是在美国用航空铝材手工打造，无论等离子产于何家，都可以提供音箱定制服务，使其尺寸匹配相应的等离子。定制内容包括支架和喷漆。', '临兵斗者/bandr/SPL39LCR-AL.jpg', 0, '');
INSERT INTO `product` VALUES(21, 3, 'SPL618LCR-AL', '美国', '等离子电视配套扬声器', '前置LCR型，四铍高音矩阵，全铝合金外壳，最小长度59"，密封式航空铝材外壳。', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(22, 3, '63SA-7', '美国', '入天花扬声器', '6.5寸低音单元，3单元3分频入天花扬声器，178mm嵌入深度，超小型导音口设计(76mm直径圆形，或者76mm正方形），白色。', '临兵斗者/bandr/JLS_63fx.jpg', 0, '');
INSERT INTO `product` VALUES(23, 4, 'AE963', '台湾', '发烧型嵌入式扬声器，有黑色和白色两个颜色。', '', '临兵斗者/bandr/AE963_sv.jpg', 0, '');
INSERT INTO `product` VALUES(24, 4, 'AE653', '台湾', '嵌入式扬声器', '嵌入式扬声器的典范之作，令人无法抗拒的优美音质，1只 9英寸(22.9cm)碳素纤维编织低音单元，1只5.25英寸 (12.33cm)中音单元,混音腔专利设计，3英寸 (76.2mm) 纯钛膜球顶高音单元，115 watt，8 Ohms，白色，26 Hz – 20 kHz (+/- 3 dB)，ABS超厚工程塑料箱体。', '临兵斗者/bandr/AE653_shade.jpg', 0, '');
INSERT INTO `product` VALUES(25, 4, 'AP 9651', '美国', '小型私人影院专业型前置扬声器。', '全能型嵌入式扬声器，可做背景音乐和环绕音箱用，1只6.5" (16.51cm)碳素纤维编织低音单元，1只3英寸(7.6cm)钛丝膜高音单元，80 Watt，8 Ohms，38 Hz – 20 kHz (+/- 3 dB)  ，ABS工程熟料箱体，无背箱。白色。', '临兵斗者/bandr/AP9651.jpg', 0, '');
INSERT INTO `product` VALUES(26, 4, 'AP Gold', '美国', '大型私人影院全能型前置扬声器。', '专为小型私人影院设计的前置扬声器。适合长度在7至13米之间，宽度在4.5至7米之间的房间，为小型私人影院提供极致的音色表现，非常适合聆听音乐类节目。1只9英寸(22.9cm)碳素纤维编织低音单元，2只6.5英寸(16.5cm) 碳素纤维非编织中音单元和1只4英寸(10.2cm) 钛膜球顶高音单元，黑色，125 watt，25.4mm MDF木质箱体，可搭配AE 963作为环绕扬声器。', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(27, 4, 'AP 21133', '美国', '超大型私人影院专业型前置扬声器。', '专为大型私人影院设计的全能型前置扬声器，音质无与伦比。适合长度在10到16米之间，宽度至少为5米的房间。为大型私人影院提供极致的音色表现，非常适合聆听音乐类节目。2只9英寸 (22.9cm)碳素纤维编织低音单元，2只5.5英寸 (14cm) 中音单元和2只4英寸 (10.2cm)钛膜球顶高音单元，黑色，150 watt，8 Ohm，40Hz – 20 kHz (+/- 3 dB)，25.4 MDF木质箱体，安装高度离地至少76 cm，房间宽度如果小于9米可以匹配 AP 8251，AP 751和AE 963', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(28, 4, 'SB 12SE', '美国', '小型私人影院专用超低音扬声器（特制版）', '专为超大型私人影院所设计的号角前置扬声器，淋漓尽致表现好莱坞电影音效。适合长度在15-22米之间，宽度最少为5.5米的房间。为影院中每个座位都提供令人叹为观止的对白清晰度。黑色，250 watt，8 Ohms，36 Hz – 21 kHz (+/- 3 dB)，2只11英寸(28cm) 碳素纤维编织低音单元,11.5” x 6.5” (29cm x 16.5cm) 铝制号角中音单元，2只号角高音单元，水平覆盖面90度，垂直覆盖面60度，25.4mm MDF木箱结构，安装高度距地至少62cm，房间宽度大于7', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(29, 4, 'SB 215', '美国', '超大型私人影院专用超低音扬声器。', '专为小型私人影院所设计的专业超低音扬声器，使用4只效果最佳，特制版的低频音色更加温暖自然，12英寸 (30.5cm)顶级纸盆单元，2英寸(5.08cm)大音圈和and 150 oz (4.25kg)磁体，500 watts ，4 Ohms，22 Hz – 425 Hz (+/- 3 dB)，25.4mm MDF木质箱体，黑色，特制版播放电影和音乐节目都有最佳表现。', '临兵斗者', 0, '');
INSERT INTO `product` VALUES(30, 4, 'AP Platinum ', '美国', '超大型私人影院全能型前置扬声器。', '专为超大型私人影院所设计的专业超低音扬声器，推荐使用2-4只，无与伦比的低频冲击感受，2 只15英寸(38.1cm)碳素纤维非编织单元，5英寸 (12.2cm)超大音圈和150 oz (4.25kg)磁体，800 watts，4 Ohms，18 Hz – 290 Hz (+/- 3 dB)，25.4mm MDF木质箱体，黑色。', '临兵斗者/bandr/APPLAT.jpg', 0, '');
