INSERT INTO `product` VALUES(140, 25, 'BDP-140', '中国', '3D蓝光播放，X.V.COLOR,\r\n36bit deep color\r\nHD 7.1环绕声\r\n便捷的网络功能\r\n高级数字音频修正\r\nipod/ipad,iphone的便捷遥控功能\r\n安卓手机系统的便捷遥控功能\r\n支持多种格式视频播放\r\n', '', '临兵斗者/bandr/xf.jpg', 0, '');
INSERT INTO `product` VALUES(141, 26, 'BDP-93', '中国', '1.卓越不凡的图像与音质效果\r\n2.双HDMI输出， 红外遥控及RS232标准控制\r\n3.支持蓝光3D播放及网络流媒体\r\n4.支持SACD、DVD-Audio/Video、HDCD、WAV/FLAC\r\n5.第二代Qdeo视频处理技术\r\n6.支持Weless-N、eSATA及USB2.0', '', '临兵斗者/bandr/oppo1.jpg', 0, '');
INSERT INTO `product` VALUES(142, 26, 'BDP-95', '中国', '1.ES9018 SABRE 32位监听级数字模拟转换器\r\n2.XLR平衡音频输出及Rotel定制环形变压器\r\n3.支持SACD、DVD-Audio/Video、HDCD、WAV/FLAC\r\n4.第二代Qdeo视频处理技术\r\n5.支持3D、流媒体、eSATA及USB2.0\r\n6..双HDMI输出， 红外遥控及RS232标准控制', '', '临兵斗者/bandr/oppo2.jpg', 0, '');
