INSERT INTO `product` VALUES(148, 28, 'P-6900ARE', '中国', '高级稳压器/电源调节器。SMP高级浪涌保护  ，线性滤波技术 ， EVS电压保护，  真有效值电压调节  ，11个插座  ，额定30A ， 诊断灯，  USB充电口， 隔离排插 ， 双电压/电流计', '', '临兵斗者/bandr/furman2.jpg', 0, '');
INSERT INTO `product` VALUES(149, 28, 'PS-8RE', '中国', '10 安培电源处理器、定序器:线性滤波技术 (LiFT)/序列多级保护技术 /极端电压自动关闭电路 (EVS)、后备BNC 鹅颈管灯连接器', '', '临兵斗者/bandr/furman3.jpg', 0, '');
INSERT INTO `product` VALUES(150, 28, 'PS-PROE', '中国', '16 安培电源处理器、定序器:3-段电源开/关，机架安装/线性滤波技术 (LiFT)/序列多级保护技术 /极端电压自动关闭电路 (EVS)、后备BNC鹅颈管灯连接器/按键锁定开关', '', '临兵斗者/bandr/furman4.jpg', 0, '');
INSERT INTO `product` VALUES(151, 29, '1.5mm2X4C', '中国', '', '1>本品导体采用足横裁面积的CCC高纯无氧铜和低损耗绝缘介质,能真实,准确重播音频信号；2>本品在结构上采用星形对称结构，提供了线材更低的分布电容，抗外部电磁波的能力极优；3>圆形加强双绝缘结构，确保线材的防火性能之外更延长了线缆本身的使用寿命;4>专用创新型防尘耐用护套，更易保持线材外观的清洁；', '临兵斗者/bandr/maxvision1.jpg', 0, '');
INSERT INTO `product` VALUES(152, 29, '2.5mm2X2C', '中国', '', '1>本品导体采用足横截面积的CCC高纯无氧铜和低损耗绝缘介质,能真实,准确重播音频信号；2>本品在结构上采用扁平结构，适应于安装位置有要求的场合；3>加强双绝缘结构，确保线材的防火性能之外更延长了线缆本身的使用寿命;4>专用创新型防尘耐用护套，更易保持线材外观的清洁；', '临兵斗者/bandr/maxvision2.jpg', 0, '');
INSERT INTO `product` VALUES(153, 29, 'HDMI', '中国', '', '1>本司HDMI产品导体采用光面的单支的CCC高纯无氧铜或是镀银单支铜,精密稳定的高发泡绝缘介质,以期在高频传输过程中能提供更稳定有效的特性阻抗和更为合理的插入损耗；2>在屏蔽型式上采用了均匀稳定的全包围结构,以期在极高频段上处理掉特性阻抗转移过多的问题；3>我司HDMI线缆产品出厂前全部采用专用HDMI TESTER进行刷机测试,现目前全完迎合HDMI 1.4高速带网络类标准(High Speed With Ethernet),产品能完美支持1080P,36Bit色深;4>专用创新型防尘耐用护套，更易保', '临兵斗者/bandr/maxvision3.jpg', 0, '');
INSERT INTO `product` VALUES(154, 30, 'DSP-100SE', '中国', '\r\n', '开启、调整好ECHO回音的效果，再启动REBERB残响功能即产生SPACE ECHO空间回音的效果，您可去调整您需要的DEEP空间的深度，再去调整DRY空间变化的时间差，再加上程序中所设定的频率移动变化，即成为最新发表的SPACE ECHO空间回音效果。\r\n1. 采用2012最新SPACE ECHO 空间回音效果版, 功能强,操作简单.\r\n2. 具ECHO回音与REBERB残响功能,效果可单独使用,或混合使用.\r\n3. 开启、调整好ECHO回音的效果，再启动REBERB残响功能即产生SPACE ECHO空间回音', '临兵斗者/bandr/bypass1.jpg', 0, '');
INSERT INTO `product` VALUES(155, 30, 'SK-2', '中国', '专业KARAOKE包房喇叭,家庭剧院环绕喇叭,室内外公播喇叭,\r\n特性：具HI-FI音乐性、低频延伸好、人声饱满度佳\r\n\r\n◎音路/设计： 3 way, 3 driver, \r\n◎单元尺寸 :  Low: 10吋X1, Mid: 3吋X1 Hi: 3吋X1\r\n◎频率响应： 45 HZ –17 KHz\r\n◎平均阻抗： 8奥姆\r\n◎灵敏度： 91 dB\r\n◎建议驱动功率：20 - 150 瓦\r\n◎外观尺寸：300 × 500 × 280 mm（高.宽.深.）\r\n◎重量： 12 Kg / 每只\r\n', '', '临兵斗者/bandr/bypass2.jpg', 0, '');
INSERT INTO `product` VALUES(156, 30, 'SK-4', '中国', '专业KARAOKE包房喇叭,家庭剧院环绕喇叭,室内外公播喇叭,\r\n特性：效率高、穿透力好、MIC好入声、人声饱满度佳\r\n◎音路/设计： 2 way, 4 driver, \r\n◎单元尺寸 :  Low: 8吋X2, Hi: 3吋X2\r\n◎频率响应： 50 HZ –17 KHz\r\n◎平均阻抗： 4奥姆\r\n◎灵敏度： 95 dB\r\n◎建议驱动功率：25 - 180 瓦\r\n◎外观尺寸：340 × 510 × 300 mm（高.宽.深.）\r\n◎重量： 16 Kg / 每只\r\n', '', '临兵斗者/bandr/bypass3.jpg', 0, '');
INSERT INTO `product` VALUES(157, 31, 'PGX24/SM58', '中国', '同时使用多达12个通(频)道，并为一托二的拾音传输模式；微处理器控制分集；静噪控制；话筒/线性电平键；LXR和1/4英寸输出；带电平控制；全金属底座，坚固耐用； RF负载频率范围：524-865MHz \r\n 工作范围：在标准条件下为100米典型距离，487.6米为视线内。\r\n 音频响应：45到15000 Hz，+2Db。注：所有系统频率响应依据话筒型号不同而定。', '', '临兵斗者/bandr/sr1.jpg', 0, '');
INSERT INTO `product` VALUES(158, 31, 'PG288/PG58', '中国', '可靠的无线性能；多达 8 个兼容系统（跨多频带）；舒尔专利技术“预分集接收”保证接收的连续性；可靠、便携的设置；内置天线；内置 1/4 波长天线将错误定位和断裂的机率降至最低；耐用的结构；卓越的音质；可变的扩展比率，音质清晰；', '', '临兵斗者/bandr/sr2.jpg', 0, '');
INSERT INTO `product` VALUES(159, 32, 'IS10+', '中国', '\r\nIS10＋全频音箱\r\n● Beyma & Celestion 单元配置\r\n● DMFTM复合材料高音号角设计，90°x60°(H°xV°)可旋转\r\n● 简单易用的吊挂配件支持\r\n● 适用于主扩声(FOH)和舞台监听\r\n● 结实、防潮的俄罗斯进口桦木夹板CNC箱体\r\n● UltraTexTM专利环保水性砂纹漆\r\n● 极低的3次谐波失真(声压110dB时低于0.6%)\r\n● 频率响应:72Hz～19000Hz±3dB\r\n● 额定功率:250W(AES标准)                           IS+系列可满足', '', '临兵斗者/bandr/al1.jpg', 0, '');
INSERT INTO `product` VALUES(160, 32, 'IS12+', '中国', '\r\nIS12＋全频音箱\r\n● Beyma & Celestion 单元配置\r\n● DMFTM复合材料高音号角设计，90°x60°(H°xV°)可旋转\r\n● 简单易用的吊挂配件支持\r\n● 适用于主扩声(FOH)和舞台监听\r\n● 结实、防潮的俄罗斯进口桦木夹板CNC箱体\r\n● UltraTexTM专利环保水性砂纹漆\r\n● 极低的3次谐波失真(声压110 dB时低于0.6%)\r\n● 频率响应:55Hz～18000Hz -6dB\r\n● 额定功率:450W (AES标准)                                ', '', '临兵斗者/bandr/al2.jpg', 0, '');
INSERT INTO `product` VALUES(161, 32, 'PRO6.0', '中国', '\r\nPRO6.0\r\n● 灵敏度:0.775V/36dB/32dB                      \r\n● 滤波电解容量:10000μf/63V×8\r\n● 阻尼系数≥500\r\n● 转换速率≥45V/μs                      \r\n● 额定功率: 8Ω立体声:2×560W, 4Ω立体声 :2×850W ,2Ω立体声 :2×1100W.\r\n\r\n\r\n', '', '临兵斗者/bandr/奥雷3.jpg', 0, '');
INSERT INTO `product` VALUES(162, 33, '点唱机', '中国', '', '雷客精灵 ROCK-Q系列稳定 高端 专业 时尚 全能王\r\n具备超大容量硬盘 2TB 支持最新3T硬盘（内测中，近期公布）\r\n内置三万五千首普清 两万首高清720P HDMI版\r\n高清MTV 原人原唱 有授权（含1080P高清歌曲）\r\n支持手写 笔划 笔画 歌词 歌星 曲种等多种点歌机方式\r\n处理器采用400MHZ高速，滑动够流畅\r\n支持高性能图像处理技术，\r\n七种机顶盒外观 黑白红粉银蓝绿\r\n滑动 拖拽 画中画 HDMI 无线 手机 平板 神马最给力。', '临兵斗者/bandr/lk1.jpg', 0, '');
INSERT INTO `product` VALUES(163, 34, 'Z-800350SD', '中国', '', '冲压五金与天然橡胶结构件，天花，墙壁，地板均适用。               \r\n五金件厚2毫米，镀镍，橡胶为进口胶。（减震器加减震龙骨后高度35mm）               \r\n用于解决室内隔声切断声桥，隔声密闭空腔使用，及弹性墙体用。', '临兵斗者/bandr/LHZ.jpg', 0, '');
INSERT INTO `product` VALUES(164, 34, 'Z-804560SD-45', '中国', '厚度0.45mm，长度2500mm/根，适用于墙壁或天花配套。', '', '临兵斗者/bandr/LHZ.jpg', 0, '');
INSERT INTO `product` VALUES(165, 34, 'Z-804560SD-60', '中国', '厚度0.60mm，长度2500mm/根，适用于地面，墙壁或天花配套。', '', '临兵斗者/bandr/LHZ.jpg', 0, '');
