INSERT INTO `product` VALUES(169, 42, '2250i', '加拿大', '定制型运动装置', '强健、精确、可靠；轻量型；低功耗；可调运动强度双轴向运动系统；支持电影和游戏。', '临兵斗者/bandr/2250i.jpg', 0, '');
INSERT INTO `product` VALUES(170, 42, '3250i', '加拿大', '定制型运动装置', '强健、精确、可靠；轻量型；低功耗；可调运动强度双轴向运动系统；支持电影和游戏。', '临兵斗者/bandr/3250i.jpg', 0, '');
INSERT INTO `product` VALUES(171, 42, 'SERIES IV BD', '加拿大', '动感控制器', '内置500G硬盘；内置BD蓝光光驱可读运动码；配有RJ45网络接口可网络升级；可远程控制带有RS232接口；可自动升级运动码库；轻量而且可靠；支持的系统WIN2000、XP、Vista、WIN7（32bit）。', '临兵斗者/bandr/SERIESIVBD.jpg', 0, '');
INSERT INTO `product` VALUES(172, 42, 'MCI 2P', '加拿大', '动感控制器', '内置500G硬盘；内置BD蓝光光驱可读运动码；配有RJ45网络接口可网络升级；可远程控制带有RS232接口；可自动升级运动码库；轻量而且可靠；支持的系统WIN2000、XP、Vista、WIN7（32bit）。', '临兵斗者/bandr/MCI2P.jpg', 0, '');
INSERT INTO `product` VALUES(173, 42, 'MFX‐ILC', '加拿大', '扩展设备', '易于安装适合新的座椅；控制级别显示；运动和震动调节；声音强度调节；待机状态灯光自动减弱；通过软件强度控制；轻便可携带；通过了微软认证（WHQL）。', '临兵斗者/bandr/MFX‐ILC.jpg', 0, '');
INSERT INTO `product` VALUES(174, 36, 'A480SYS', '中国', '', 'A480SYS是基于UH480水平拓展镜头的全套电动套装，该镜头结合了圆形透镜和棱镜的双重优点，使光损失量低至2%，而色彩失真也减至最小。电动滑轨可以通过红外遥控器或者12伏触发装置进行控制，实现不同画面比例之间的切换。', '临兵斗者/bandr/panamorph-A480SYS.jpg', 0, '');
INSERT INTO `product` VALUES(175, 36, 'UH480', '中国', '', 'UH480是Panamorph荣耀的水平拓展镜头，该镜头结合了圆形透镜和棱镜的双重优点，使光损失量低至2%，而色彩失真也减至最小。还可根据不同的投影距离定制对焦修正镜片。', '临兵斗者', 0, '');
