INSERT INTO `product` VALUES(177, 36, 'UV200', '中国', '', 'UV200是Panamorph旗下一款高性价比的小镜头，采用纵向压缩的原理实现2.35:1电影画面，画面失真度控制在极低的状态。', '临兵斗者/bandr/panamorph-UV200.jpg', 0, '');
