INSERT INTO `site_info` VALUES(1, '者尼', '', '者尼,者尼者尼者尼', '者尼——一个象征奢华与品位的品牌，成立于2007年，经过短短几年的飞速发展，现已成长为\r\n北京地区规模最大的家庭影院系统集成商，中国私人影院定制安装行业的领军企业。在项目管理以及\r\n客户服务等方面积累了丰富而成熟的经验，与2010年在山西设立分公司，并与上海，南京，成都等地\r\n经销商达成战略合作关系。', '', '', '', '© 2012 Zene Tech Co.,Ltd.All Rights Reserved.', '', '', '136187101@qq.com', '                         ', '车                                        ', '');
