INSERT INTO `zhiwei` VALUES(1, '技术经理1', '<p>\r\n	1工作经验：有工作经验者优先\r\n</p>\r\n<p>\r\n	工作性质：全职\r\n</p>\r\n<p>\r\n	招聘人数：不限\r\n</p>\r\n<p>\r\n	最低学历：硕士\r\n</p>\r\n<p>\r\n	岗位职责:\r\n</p>\r\n<p>\r\n	1、负责区域内市场大客户的管理与维护，新客户的拓展与开发；\r\n</p>\r\n<p>\r\n	2、区域内大客户合同谈判与执行，回款跟进与执行，完成销售任务；\r\n</p>\r\n<p>\r\n	3、区域内大客户其他事项的跟进与执行；\r\n</p>\r\n<p>\r\n	任职资格:\r\n</p>\r\n<p>\r\n	1、坦诚自信，乐观进取，高度的工作热情；\r\n</p>\r\n<p>\r\n	2、有良好的团队合作精神，有敬业精神；\r\n</p>\r\n<p>\r\n	3、具有独立的分析和解决问题的能力；\r\n</p>\r\n<p>\r\n	4、较强的人际沟通能力，并能够适应长期出差。\r\n</p>\r\n<p>\r\n	5、勤奋、真诚，具有挑战精神。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>', '2012-04-26 14:28:14', 1);
INSERT INTO `zhiwei` VALUES(3, '技术专家', '<p>\r\n	<span style="font-size:14px;">工作经验：有工作经验者优先&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;工作性质：全职</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">招聘人数：不限&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最低学历：不限</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">岗位职责:</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">1、</span><span style="font-size:14px;">负责全国技术服务体系规划、建设、运营与管理，提高技术服务质量；</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">2</span><span style="font-size:14px;">、负责开展客户关爱、互动和客户关系管理；</span><br />\r\n<span style="font-size:14px;">3</span><span style="font-size:14px;">、负责组织人员开展客户开发、保留和发展；</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">4、</span><span style="font-size:14px;">主持售后管理部的日常工作，维护和及时更新技术服务工作流程和作业指导书，提升服务质量；</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">5、</span><span style="font-size:14px;">负责公司技术服务体系的规划和建设，组建有效的公司技术服务网络；</span>\r\n</p>\r\n<p>\r\n	<span style="font-size:14px;">6、</span><span style="font-size:14px;">负责公司技术服务团队的建设和管理，负责本部门的岗位设置、人员聘用、培训和考核，对技术主任实行定期调访；</span>\r\n</p>\r\n<p>\r\n	<p>\r\n		<p>\r\n			<p>\r\n				<p>\r\n					<span style="font-size:14px;">&nbsp;</span>\r\n				</p>\r\n			</p>\r\n		</p>\r\n	</p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<span style="font-size:14px;">任职资格</span><span style="font-size:14px;">:&nbsp;</span> \r\n<p>\r\n	<span style="font-size:14px;">1</span><span style="font-size:14px;">、</span><span style="font-size:14px;">有</span><span style="font-size:14px;">8</span><span style="font-size:14px;">年以上</span><span style="font-size:14px;">规模养殖场工作</span><span style="font-size:14px;">经验者优先；&nbsp;</span><br />\r\n<span style="font-size:14px;">2</span><span style="font-size:14px;">、较强的管理、沟通、协调能力；&nbsp;</span><br />\r\n<span style="font-size:14px;">3</span><span style="font-size:14px;">、工作认真、严谨、有条理、有思路。</span>\r\n</p>', '2012-04-26 14:29:34', 0);
