INSERT INTO `essay` VALUES(9, 7, 'CK800F', '', 'fetal Monitor', 'newspic/thumb_52662.jpg', '', '', '', '', '', '', '', '', '<p>&nbsp;<img src="http://www.contecmed.com/main/wangxuebin_admint/UploadFiles/200951391817552.jpg" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="font-size:x-small;color:#ff0000;"><span style="color:#ff9900;">Features:</span></span></strong></p>\r\n<p><span style="font-size:x-small;">■ Single or Twins Ultrasound transducer</span></p>\r\n<p><span style="font-size:x-small;">■ Data Graph and Trend Table Review of both mother and fetal</span></p>\r\n<p><span style="font-size:x-small;">■ Built-in thermal printer</span></p>\r\n<p><span style="font-size:x-small;">■ Built-in Network capability for connect with central monitoring software</span></p>\r\n<p><span style="font-size:x-small;">■ Programmable alarms</span></p>\r\n<p><span style="font-size:x-small;">■ All the colors and looks or the parameters can be customsized</span></p>\r\n<p><span style="font-size:x-small;">■ Multi-face views, select focus on mother or fetal</span></p>\r\n<p><span style="font-size:x-small;">■ Storage of patient information and data</span></p>\r\n<p><span style="font-size:x-small;color:#e61a1a;"><strong><span style="color:#ff9900;">Performance Specifications:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Display:8.4" Color TFT</span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:640x480</span></p>\r\n<p><span style="font-size:x-small;">■ Display Mode:Standard View,Fetal View,Mother View </span></p>\r\n<p><span style="font-size:x-small;">■ Indicator:Power indicator light, Alarm sound</span></p>\r\n<p><span style="font-size:x-small;">■ Interface:Socket for connecting fetal''s FHR, TOCO, and Fetal Movement sensorSocket for</span><span style="font-size:x-small;">&nbsp; connecting mother''s ECG, NIBP and&nbsp;&nbsp; SPO2 sensor.Net Socket(RJ45) for communication</span><span style="font-size:x-small;"> cable with Central Monitoring Station Sofrware.</span></p>\r\n<p><span style="font-size:x-small;">■ Power Supply:AC 100 ~ 240V, 50/60Hz, Powe &lt; 60VA</span></p>\r\n<p><span style="font-size:x-small;">■ Trend Graph:Resolution from 1s, 5s, 10s. Maximum time 96 hours.</span></p>\r\n<p><span style="font-size:x-small;">■ Trend Table:Resolution from 1s, 5s, 10s. Review up to 1000 items.</span></p>\r\n<p><span style="font-size:x-small;">■ Alarm:Ajustable High and Low limits. Three level audible and vistual alarm. </span></p>\r\n<p><span style="font-size:x-small;">■ User Configuration:All the color of parameters and waves can be set according to&nbsp;</span><span style="font-size:x-small;">user''s willing.</span></p>\r\n<p><span style="font-size:x-small;color:#ee1111;"><span style="color:#ff9900;"><strong>Technical specifications:</strong></span></span></p>\r\n<p><span style="font-size:x-small;">■ Safety</span></p>\r\n<p><span style="font-size:x-small;">■ Meet the requirment of IEC60601 series</span></p>\r\n<p><span style="font-size:x-small;">■ Type of Protection:Class II with internal electirc power supply</span></p>\r\n<p><span style="font-size:x-small;">■ Degree of Protection:BF</span></p>\r\n<p><span style="font-size:x-small;">■ Dimention and Weight</span></p>\r\n<p><span style="font-size:x-small;">■ Dimention:92(W)x82(H)x22(D)mm</span></p>\r\n<p><span style="font-size:x-small;">■ Weight:3.6kg(with battery)</span></p>\r\n<p><span style="font-size:x-small;color:#e61a1a;"><span style="color:#ff9900;"><strong>Operation Environment:</strong></span></span></p>\r\n<p><span style="font-size:x-small;">■ Temperature:0℃~ +40℃</span></p>\r\n<p><span style="font-size:x-small;">■ Humidity:15% ~ 95%</span></p>\r\n<p><span style="font-size:x-small;color:#ee1111;"><span style="color:#ff9900;"><strong>Storage Environment:</strong></span></span></p>\r\n<p><span style="font-size:x-small;">■ Temperature:-20℃~ +60℃</span></p>\r\n<p><span style="font-size:x-small;">■ Humidity:10% ~ 95%&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p>\r\n<p><span style="font-size:x-small;">■ Patient Range:Pregnant Adult</span></p>\r\n<p><span style="font-size:x-small;color:#e61a1a;"><strong><span style="color:#ff9900;">FHR:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Ultrasound Frequency:2MHz</span></p>\r\n<p><span style="font-size:x-small;">■ Range:50 ~ 210 bpm</span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:1bmp</span></p>\r\n<p><span style="font-size:x-small;">■ Accuracy:±2bmp</span></p>\r\n<p><span style="font-size:x-small;color:#dd2222;"><strong><span style="color:#ff9900;">TOCO:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Range:0 ~ 100% </span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:1%</span></p>\r\n<p><span style="font-size:x-small;">■ Non-linear Error:&lt;=±8%</span></p>\r\n<p><span style="font-size:x-small;">■ Zero control:Manual</span></p>\r\n<p><span style="font-size:x-small;">FETAL&nbsp;&nbsp; MOVEMENT:</span></p>\r\n<p><span style="font-size:x-small;">■ Manual fetal movement mark</span></p>\r\n<p><span style="font-size:x-small;color:#dd2222;"><strong><span style="color:#ff9900;">SPO2:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Measurement Range:0 ~ 100%</span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:1%</span></p>\r\n<p><span style="font-size:x-small;">■ Accuracy:±2%(70%~100%, Adult/Pediatric, non-motion)</span><span style="font-size:x-small;">±3%(70%~100%, Neonate, non-motion)0% ~ 69% unspecified</span></p>\r\n<p><span style="font-size:x-small;">■ Alarm Range:0% ~ 100%</span></p>\r\n<p><span style="font-size:x-small;">■ Refreshing Rate:1s</span></p>\r\n<p><span style="font-size:x-small;color:#e61a1a;"><strong><span style="color:#ff9900;">Pulse&nbsp; Rate:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Measurement Range:25 ~ 250 bpm</span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:1bpm</span></p>\r\n<p><span style="font-size:x-small;">■ Accuracy:±3 bpm (non-motion)</span></p>\r\n<p><span style="font-size:x-small;">■ Alarm Range:25 ~ 250 bmp</span></p>\r\n<p><span style="font-size:x-small;">■ Refreshing Rate:1s</span></p>\r\n<p><span style="font-size:x-small;color:#d52b2b;"><strong><span style="color:#ff9900;">ECG:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Lead mode:3-lead or 5-lead</span></p>\r\n<p><span style="font-size:x-small;">■ Lead selection:I, II, III, AVR, AVL, AVF, V</span></p>\r\n<p><span style="font-size:x-small;">■ HR range:15 ~ 300 bpm</span></p>\r\n<p><span style="font-size:x-small;">■ ECG Waveform:2 channals</span></p>\r\n<p><span style="font-size:x-small;">■ Accuracy:±2bpm or ±2%, which is greater</span></p>\r\n<p><span style="font-size:x-small;">■ S-T segment detection range: -2.0mv ~ +2.0mv</span></p>\r\n<p><span style="font-size:x-small;">■ Arrhythmia analysis: 13 kinds of arrhythmia</span></p>\r\n<p><span style="font-size:x-small;">■ Alarm Range:15 ~ 300 bpm</span></p>\r\n<p><span style="font-size:x-small;color:#e61a1a;"><strong><span style="color:#ff9900;">NIBP:</span></strong></span></p>\r\n<p><span style="font-size:x-small;">■ Method:Oscillometry</span></p>\r\n<p><span style="font-size:x-small;">■ Mode:Manual/Auto/Contiuous</span></p>\r\n<p><span style="font-size:x-small;">■ Measurement Range:25 ~ 260mmHg</span></p>\r\n<p><span style="font-size:x-small;">■ Atuo Measure Interval: 5, 10, 15, 30, 45, 60, 90 minutes</span></p>\r\n<p><span style="font-size:x-small;">■ Resolution:1mmHg</span></p>\r\n<p><span style="font-size:x-small;">■ Overpressure Protection: 300mmHg</span></p>\r\n<p><span style="font-size:x-small;">■ Alarm range:25 ~260mmHg</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:x-small;"><img src="http://www.contecmed.com/main/images/UploadFile/2009824144045885.jpg" /></span></p>', 2, NULL, NULL, '1310008016', 0, 1);
