INSERT INTO `essay` VALUES(12, 8, ' Sonoline C2 ', '', '', '../../newspic/thumb_45982.gif', '', '', '', '', '', '', '', '', '<p><img src="http://www.contecmed.com/main/wangxuebin_admint/UploadFiles/201012221426994153.gif" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong><span style="font-size:small;color:#e61a1a;"><span style="color:#ff9900;">Instructions</span></span></strong></p>\r\n<strong><span style="font-size:small;color:#e61a1a;"> <p><br />\r\n</span></strong>Pocket Fetal Doppler is a hand-held obstetrical unit, which can be used in hospital, clinic and home for daily self-check by pregnant woman.</p>\r\n<p><br />\r\nThe device uses color LCD of high resolution to display the fetal heartbeat waveform, integrated the function of time display and menu control, calculating the FHR, FHR data, heartbeat waveform and heartbeat sound memory, wave review, and it can communicate with PC, transfer the data to PC software to display and print the heart rate trend graph, which can help the doctor diagnose and manage the case in time.</p>\r\n<p><br />\r\nIt contains components of ultrasonic signal transmitter and receiver, analog signals processing unit, FHR calculating unit, LCD display control unit, efficient signal memory and read etc. It has 3 work modes: real-time FHR display mode, averaged FHR display mode, and manual mode. It also has audio output, and can be connected with earphone or recorder with audio input.</p>\r\n<p><br />\r\n<span style="font-size:small;color:#e61a1a;"><strong><span style="color:#ff9900;">Main Features</span></strong></span></p>\r\n<span style="font-size:small;color:#e61a1a;"><strong> <p><br />\r\n</strong></span>■Beautiful shape, portable, easy operation<br />\r\n■real-time clock display<br />\r\n■three test modes: real-time, average, manual<br />\r\n■Chinese and English display and adjust<br />\r\n■The probe has bending structure which is easy to operate and can increase the ease of the pregnant women, incarnating the humane design<br />\r\n■The probe can be changeable<br />\r\n■Probe inspection <br />\r\n■FHR values, bar graph and heartbeat waveform color screen display<br />\r\n■Alarming in red when the FHR range is out of the normal range<br />\r\n■memory and review<br />\r\n■observe the heart rate trend graph by PC software, manage the memory cases<br />\r\n■Battery status indicator<br />\r\n■Built-in speaker<br />\r\n■Output for headphone.<br />\r\n■Auto power-off when there is no operation in one minute<br />\r\n■Two pieces of standard 1.5V alkaline battery available which can work no less than 8 hours</p>\r\n<p><br />\r\n<span style="font-size:small;color:#e61a1a;"><strong><span style="color:#ff9900;">Main performance</span></strong></span></p>\r\n<span style="font-size:small;color:#e61a1a;"><strong> <p><br />\r\n</strong></span>■Display: 1.77"262K TFT display<br />\r\n■FHR Performance:&nbsp;FHR Measuring Range: 50~240BPM (BPM: beat per minute)&nbsp;Resolution: 1 BPM&nbsp;Accuracy: ± 2 BPM<br />\r\n■Power consumption: &lt; 1 W<br />\r\n■Auto Shut-OFF: Automatic power-off when there is no operation in one minute<br />\r\n■Battery Type Recommended: TWO 1.5V（AA size）alkaline battery<br />\r\n■Probe:<br />\r\n&nbsp;&nbsp;&nbsp; Working Frequency: 2.0 MHz ± 10%<br />\r\n&nbsp;&nbsp;&nbsp; Ultrasonic Output Intensity: Isata &lt; 5 mW/cm<sup>2<br />\r\n</sup>&nbsp;&nbsp;&nbsp; Ultrasonic Output Power: P &lt; 10 mW<br />\r\n&nbsp;&nbsp;&nbsp; Working Mode: Continuous<br />\r\n&nbsp;&nbsp;&nbsp; Effective Radiating Area of Transducer: 157 mm<sup>2</sup> ± 15%</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp; Physical Characteristic</p>\r\n<p><br />\r\nDimension: (L) 135mm × (W) 92mm × (H)29mm <br />\r\nWeight: About 245g (including batteries) </p>\r\n<p>&nbsp;</p>', 0, NULL, NULL, '1310020631', 0, 1);
INSERT INTO `essay` VALUES(16, 12, 'Multi-para handheld monitor', '', '', '../../newspic/thumb_70345.jpg', '', '', '', '', '', '', '', '', '<p><strong>Model:CAH5000</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="color:#ff9900;"><strong>Features and functions&nbsp;&nbsp;&nbsp;:</strong></span></p>\r\n<p><br />\r\n<span style="font-size:14px;font-family:Arial;">1.&nbsp;Palm,&nbsp;compact,&nbsp;easy&nbsp;for&nbsp;carrying&nbsp;and&nbsp;operation</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">2.&nbsp;Clear&nbsp;and&nbsp;bright&nbsp;color&nbsp;LCD&nbsp;with&nbsp;real&nbsp;time&nbsp;display&nbsp;</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">3.&nbsp;Friendly&nbsp;user&nbsp;interfaces&nbsp;with&nbsp;touch&nbsp;screen</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">4.&nbsp;Intelligent&nbsp;Measuring&nbsp;of&nbsp;ECG/NIBP/SpO2/PR/PLETH/TEMP</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">5.&nbsp;Storage/Review&nbsp;trend&nbsp;data&nbsp;up&nbsp;to&nbsp;129&nbsp;hours&nbsp;and&nbsp;3888&nbsp;groups&nbsp;</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">6.&nbsp;Utility&nbsp;PC&nbsp;software&nbsp;package&nbsp;for&nbsp;long&nbsp;time&nbsp;monitoring,&nbsp;web&nbsp;transferring,&nbsp;history&nbsp;data&nbsp;uploading/review/printing/storage.</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">7.&nbsp;Audio&nbsp;and&nbsp;visual&nbsp;alarm</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">8.&nbsp;Built-in&nbsp;rechargeable&nbsp;Li-ion&nbsp;battery,&nbsp;up&nbsp;to&nbsp;5-hour&nbsp;power&nbsp;supply</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">9.&nbsp;Automatic&nbsp;shutdown&nbsp;for&nbsp;power&nbsp;saving</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="color:#ff9900;"><strong>Specifications:</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>ECG</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;color:#000000;">Input3-&nbsp;lead&nbsp;ECG&nbsp;cable,&nbsp;standard&nbsp;AAMI&nbsp;cable&nbsp;connector</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Lead&nbsp;SelectionI,II,III</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Gain&nbsp;Selection×1,×2,×4,×8</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Sweep&nbsp;Speed:&nbsp;12.5,&nbsp;25,&nbsp;and&nbsp;50&nbsp;mm/s</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Frequency&nbsp;Response:&nbsp;None&nbsp;filter:&nbsp;0.05~100Hz&nbsp;(-3dB),&nbsp;filter:&nbsp;0.5~40Hz&nbsp;(-3dB)</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Calibration&nbsp;Signal:&nbsp;1mV&nbsp;square&nbsp;wave&nbsp;</span></p>\r\n<p><span style="font-size:14px;color:#000000;">Protection:&nbsp;Against&nbsp;electrosurgical&nbsp;interference&nbsp;and&nbsp;defibrillation</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;color:#ff9900;"><strong><span style="font-size:12px;color:#000000;">Heart&nbsp;Rate</span></strong></span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Measuring&nbsp;Range:&nbsp;30~250&nbsp;bpm</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Renew&nbsp;Time:&nbsp;1&nbsp;time&nbsp;per&nbsp;beat</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Accuracy:&nbsp;±1&nbsp;bpm</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Alarm&nbsp;Mode:&nbsp;Audible&nbsp;and&nbsp;visual&nbsp;alarm</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>SpO2&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;font-family:Arial;">SpO2%</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Measuring&nbsp;Range:0~100%</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Resolution:1&nbsp;%</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Accuracy:±2&nbsp;%(70%~99%),&nbsp;unspecified&nbsp;(0%~69%)</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Pulse&nbsp;Rate&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;font-family:Arial;">Measuring&nbsp;and&nbsp;Alarm&nbsp;Range:0~250bpm</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Resolution: 1bpm</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Accuracy:&nbsp;±1bpm</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>NIBP&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;">Method:&nbsp;Oscillometric</span></p>\r\n<p><span style="font-size:14px;">Mode:&nbsp;Manual,&nbsp;Auto,&nbsp;STAT</span></p>\r\n<p><span style="font-size:14px;">Measuring&nbsp;Interval&nbsp;in&nbsp;AUTO&nbsp;Mode:&nbsp;1,&nbsp;2,&nbsp;3,&nbsp;5,&nbsp;10,&nbsp;15,&nbsp;20,&nbsp;30,&nbsp;60&nbsp;(min)</span></p>\r\n<p><span style="font-size:14px;">Measuring&nbsp;Period&nbsp;in&nbsp;STAT&nbsp;Mode:&nbsp;5&nbsp;min</span></p>\r\n<p><span style="font-size:14px;">Cuff&nbsp;Pressure&nbsp;Range:0~300&nbsp;(mmHg)</span></p>\r\n<p><span style="font-size:14px;">Pulse&nbsp;Rate&nbsp;Range:30~250&nbsp;(bpm)</span></p>\r\n<p><span style="font-size:14px;">Measurement&nbsp;Rage:&nbsp;Adult&nbsp;30~255mmHg,&nbsp;Pediatric&nbsp;30~160mmHg</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Over-pressure&nbsp;Protection:&nbsp;Adult&nbsp;300mmHg,&nbsp;Pediatric&nbsp;220mmHg</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Pressure&nbsp;Resolution :&nbsp;1mmHg</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">The&nbsp;SYSTOLIC&nbsp;and&nbsp;DIASTOLIC&nbsp;values&nbsp;measured&nbsp;using&nbsp;this&nbsp;device&nbsp;shall&nbsp;be&nbsp;equal&nbsp;to&nbsp;those&nbsp; measured&nbsp;by&nbsp;trained&nbsp;medical&nbsp;personnel&nbsp;using&nbsp;cuff&nbsp;and&nbsp;stethoscope.&nbsp;The&nbsp;MEAN&nbsp;value&nbsp;measured&nbsp;</span><br />\r\n<span style="font-size:14px;font-family:Arial;">using&nbsp;this&nbsp;device&nbsp;is&nbsp;equal&nbsp;to&nbsp;that&nbsp;measured&nbsp;using&nbsp;blood&nbsp;pressure&nbsp;measurement&nbsp;device</span><strong>.&nbsp;</strong></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Temperature</strong></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size:14px;font-family:Arial;">Input:&nbsp;Body&nbsp;surface&nbsp;thermal-sensitive&nbsp;resistor&nbsp;temperature&nbsp;sensor</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Measuring&nbsp;Range:&nbsp;0~50</span></p>\r\n<p><span style="font-size:14px;font-family:Arial;">Accuracy:&nbsp;±0.1</span></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Size:&nbsp;151mm×128mm×45mm</strong></p>\r\n<p><strong>Net&nbsp;Weight:&nbsp;480g</strong></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 0, NULL, NULL, '1310022283', 0, 1);
INSERT INTO `essay` VALUES(17, 6, 'Handheld pulse oximeter', '', '', '../../newspic/thumb_30063.jpg', '', '', '', '', '', '', '', '', '<p><img style="width:229px;height:252px;" height="252" alt="" src="http://www.contecmed.com/main/wangxuebin_admint/UploadFiles/200872101852805.gif" width="177" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Model: PM60A</strong></p>\r\n<div><span style="font-size:10.5pt;"><span style="color:#d71920;"><span><span><span><span style="font-size:12pt;color:#000000;"><span style="font-size:x-small;"><span style="color:#e61a1a;"><strong><span style="font-size:14px;color:#ff9900;">Major Features&nbsp;</span></strong></span></span></span></span></span></span></span></span></div>\r\n<span style="font-size:10.5pt;"><span style="color:#d71920;"><span><span><span><span style="font-size:12pt;color:#000000;"><span style="font-size:x-small;"><span style="color:#e61a1a;"><strong> <div><br />\r\n</strong></span><span style="font-size:14px;font-family:Arial;">1.Small in volume、light in weight and convenient in carrying</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">2.Operation of the product is simple, low power consumption</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">3.SpO</span><sub><span style="font-size:14px;font-family:Arial;">2</span></sub><span style="font-size:14px;font-family:Arial;"> value display</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">4.Pulse rate value display, bar graph display</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">5.Pulse waveform display</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">6The display mode can be changed</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">7.Changeable light-time</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">8.A pulse rate sound indication</span><br />\r\n<br />\r\n<span style="font-size:14px;font-family:Arial;">9.With